using System;
using System.Globalization;
using System.Text;
using System.Text.RegularExpressions;

namespace pdb.util
{
    public static class CustomFormat
    {
        const char infini = '\u221E';
        private static CultureInfo cu = new CultureInfo("en-US");
        //4.9E+02
        private static Regex reg = new Regex(@"([0-9\.]+)E\+([0-9]+)");

        const string FORMAT = "0.### ### ### ### ### ### ### ### ### ### ### ### ###";
        public static string Format(this decimal value, int required, int desired, int limit)
        {
            return Format(value, null, required, desired, limit); 
        }
        public static string Format(this decimal value, string format, int required, int desired, int limit)
        {
            if (string.IsNullOrEmpty(format))
                format = FORMAT;
            string ret = value.ToString(FORMAT, cu);

            var sb = new StringBuilder();
            var tab = ret.ToCharArray(); int count = tab.GetLength(0);
            sb.Append(tab[0]);
            bool foundPertinent = false, foundDec = false;
            int nb =0, _nbpertinent = 0;
            for (int i = 1; i < count; i++)
            {
                var c = tab[i];
                sb.Append(c);
                if (c == '.' ||c == ',' )
                {
                    foundDec = true; 
                    continue;
                }
                if (c == ' ')
                    continue;
                nb++;
                if (c != '0')
                {
                    if (!foundPertinent)
                    {
                        foundPertinent = true;
                    }
                    _nbpertinent++;
                }
                else
                {
                    if (foundPertinent)
                        _nbpertinent++;
                }
                if (foundDec)
                {
                    if (nb >= limit)
                        break;
                    if (_nbpertinent >= required && nb >= desired)
                        break;
                }


            }

            ret = sb.ToString().Trim().Replace(" ", ","); 
            return ret; 
        }

        public static string FormatCustom(this decimal value, int nb)
        {
            return value.FormatCustom(nb, nb, nb);
        }

        public static string FormatCustom(this double value, int nb)
        {
            return value.FormatCustom(nb, nb, nb);
        }



        public static string FormatCustom(this double value, int nb, int nbMax)
        {
            return value.FormatCustom(nb, nb, nbMax);
        }

        public static string FormatCustom(this decimal value, int nb, int nbMax)
        {
            return value.FormatCustom(nb, nb, nbMax);
        }
        public static string FormatCustom(this double value, int nb, int nbMin, int nbMax)
        {
            if (value.Equals(double.MinValue))
                return "-" + infini;
            if (value.Equals(double.MaxValue))
                return infini.ToString();

            if (nbMin < nb)
            {
                if (Math.Abs(value) < Math.PowD(10, nbMin - 1))
                {
                    return FormatCustom(value, nbMin, nbMin, nbMax);
                }
            }

            string ret = value.ToString("G" + nb, cu);
            if (ret.StartsWith("0."))
            {
                ret = ret.Remove(0, 1);
                return ret;
            }

            Match m = reg.Match(ret);
            if (m.Success)
            {
                string strNumber = m.Groups[1].Value;
                string strExp = m.Groups[2].Value;
                if (strExp.StartsWith("0"))
                    strExp = strExp.Remove(0, 1);
                int exp = Convert.ToInt16(strExp);
                var sb = new StringBuilder();
                if (exp <= 3)
                {
                    if (exp < nbMax)
                    {
                        ret = value.ToString("#");
                        return ret;
                    }
                    double dNumber = Convert.ToDouble(strNumber, cu);

                    if (strNumber.Contains("."))
                    {
                        dNumber *= 10;
                        exp--;
                    }

                    int iNumber = Convert.ToInt16(dNumber);
                    sb.Append(iNumber);

                    for (int i = 0; i < exp; i++)
                    {
                        sb.Append("0");
                    }

                    ret = sb.ToString();
                    return ret;

                }

                sb.Append(strNumber);
                sb.Append("e");
                sb.Append(strExp);

                ret = sb.ToString();
                return ret;
            }


            return ret;
        }

        public static string FormatCustom(this decimal value, int nb, int nbMin, int nbMax)
        {
            if (value.Equals(double.MinValue))
                return "-" + infini;
            if (value.Equals(double.MaxValue))
                return infini.ToString();

            if (value > int.MaxValue - 20)
                return infini.ToString();
            if (value < -int.MaxValue + 20)
                return "-" + infini;

            return FormatCustom(Convert.ToDouble(value), nb, nbMin, nbMax);

        }

        public static string FormatCustom(this TimeSpan value, int nb)
        {
            return value.FormatCustom(nb, nb, nb);

        }

        public static string FormatCustom(this TimeSpan value, int nb, int nbMin, int nbMax)
        {
            if (value.Equals(TimeSpan.MinValue)) return "-" + infini;
            if (value.Equals(TimeSpan.MaxValue)) return infini.ToString();
            return value.TotalDays.FormatCustom(nb, nbMin, nbMax);
        }

        public static string FormatCustomTs(this double value, int nb)
        {
            return value.FormatCustomTs(nb, nb, nb);
        }
        public static string FormatCustomTs(this decimal value, int nb)
        {
            return value.FormatCustomTs(nb, nb, nb);
        }

        public static string FormatCustomTs(this double value, int nb, int nbMin, int nbMax)
        {
            if (value.Equals(double.MinValue) || double.IsNegativeInfinity(value))
                return "-" + infini;
            if (value.Equals(double.MaxValue) || double.IsInfinity(value) || double.IsNaN(value))
                return infini.ToString();
            if (value > int.MaxValue - 20)
                return infini.ToString();
            if (value < -int.MaxValue + 20)
                return "-" + infini;
            TimeSpan ts = TimeSpan.FromMilliseconds(value);
            return ts.TotalDays.FormatCustom(nb, nbMin, nbMax);
        }

        public static string FormatCustomTs(this decimal value, int nb, int nbMin, int nbMax)
        {
            if (value.Equals(decimal.MinValue))
                return "-" + infini;
            if (value.Equals(decimal.MaxValue)) // || double.IsInfinity(value) || double.IsNaN(value))
                return infini.ToString();

            if (value > int.MaxValue - 20)
                return infini.ToString();
            if (value < -int.MaxValue + 20)
                return "-" + infini;

            TimeSpan ts = TimeSpan.FromMilliseconds(Convert.ToDouble(value));
            return ts.TotalDays.FormatCustom(nb, nbMin, nbMax);
        }
    }
}

using System;
using System.Collections;
using System.Collections.Generic;
using System.Xml;
using System.IO;

namespace pdb.util
{
    public class HistoValue
    {
        char SEP = '|';
        public decimal coeff;
        public decimal delta;
        public decimal accel;
        // pour info
        public decimal dcoeffp;
        public decimal conv;

        //private HistoValue(string line)
        //{
        //    var tab = line.Split(SEP);
        //    int count = tab.GetLength(0);
        //    if (count >= 1 && !string.IsNullOrEmpty(tab[0]))
        //        coeff = Convert.ToDecimal(tab[0]);
        //    if (count >= 2 && !string.IsNullOrEmpty(tab[1]))
        //        delta = Convert.ToDecimal(tab[1]) / 1000m;
        //    if (count >= 3 && !string.IsNullOrEmpty(tab[2]))
        //        conv = Convert.ToDecimal(tab[2]) / 1000m;
        //}

        public HistoValue(string line, HistoValue last)
        {
            var tab = line.Split(SEP);
            int count = tab.GetLength(0);
            string str = "";
            if (count >= 1)
            {
                str = tab[0];
                if (!string.IsNullOrEmpty(str))
                    coeff = Convert.ToDecimal(str);
                else if (last != null)
                    coeff = last.coeff;
            }
            if (count >= 2)
            {
                str = tab[1];
                if (!string.IsNullOrEmpty(str))
                    delta = Convert.ToDecimal(str) / 1000m;
                else if (last != null)
                    delta = last.delta;
            }
            if (count >= 3)
            {
                str = tab[2];
                if (!string.IsNullOrEmpty(str))
                    accel = Convert.ToDecimal(str) / 1000m;
                else if (last != null)
                    accel = last.accel;
            }

            if (count >= 4)
            {
                str = tab[3];
                if (!string.IsNullOrEmpty(str))
                    dcoeffp = Convert.ToDecimal(str) / 1000m;
                else if (last != null)
                    dcoeffp = last.dcoeffp;
            }

            if (count >= 5)
            {
                str = tab[4];
                if (!string.IsNullOrEmpty(str))
                    conv = Convert.ToDecimal(str) / 1000m;
                else if (last != null)
                    conv = last.conv;
            }
        }

        public HistoValue(decimal coeff, decimal delta, decimal accel, decimal dcoeff, decimal conv)
        {
            this.coeff = coeff;
            this.delta = delta;
            this.accel = accel;
            this.dcoeffp = dcoeff;
            this.conv = conv;
        }

        public override string ToString()
        {
            var sb = new StringBuilder();
            write(sb);
            return sb.ToString();
        }

        public void write(ITextWriter sb)
        {
            var v = Math.Round(coeff, 12);
            var sc = v.ToString();
            var v2 = Math.Round(1000m * delta, 10);
            var sc2 = v2.ToString();

            var v3 = Math.Round(1000m * accel, 10);
            var sc3 = v3.ToString();

            var v4 = Math.Round(1000m * dcoeffp, 10);
            var sc4 = v4.ToString();

            var v5 = Math.Round(1000m * conv, 10);
            var sc5 = v5.ToString();

            sb.Append(sc);
            sb.Append(SEP);
            sb.Append(sc2);
            sb.Append(SEP);
            sb.Append(sc3);
            sb.Append(SEP);

            sb.Append(sc4);
            sb.Append(SEP);
            sb.AppendLine(sc5);
        }

        private static decimal getTruncate(decimal v)
        {
            return Math.Round(1000m * v, 10);
        }

        public void writeDiff(ITextWriter sb, HistoValue last)
        {


            var v = Math.Round(coeff, 12);
            var sc = v.ToString();
            sb.Append(sc);

            sb.Append(SEP);
            var v2 = getTruncate(delta);
            if (last == null || v2 != getTruncate(last.delta))
            {
                var sc2 = v2.ToString();
                sb.Append(sc2);
            }

            sb.Append(SEP);
            var v3 = getTruncate(accel);
            if (last == null || v3 != getTruncate(last.accel))
            {
                var sc3 = v3.ToString();
                sb.Append(sc3);
            }
            sb.Append(SEP);

            var v4 = getTruncate(dcoeffp);
            if (last == null || v4 != getTruncate(last.dcoeffp))
            {
                var sc4 = v4.ToString();
                sb.Append(sc4);
            }
            sb.Append(SEP);
            var v5 = getTruncate(conv);
            if (last == null || v5 != getTruncate(last.conv))
            {
                var sc5 = v5.ToString();
                sb.Append(sc5);
            }
            sb.AppendLine();

        }
    }
