                     * */

                    //var d1 = densite;
                    //var incr = 1;
                    //var depth = 4; 

                    //while (true)
                    //{
                    //    if (d1 <= 1)
                    //        break;
                    //    depth++;
                    //    d1 /= 6; 
                    //}



                    //  densite = densite * 0.5; 
                    //else if (classement < 0.1)
                    //{ moy = 36; nbPieceByLevel = 6; }
                    //else if (classement < 0.2)
                    //{ moy = 24; nbPieceByLevel = 4; }
                    //else if (classement < 1)
