        public int NbClass { get { return DatesClassement.Count<DateTime>(t => t >= Piece.SeuilProvisoire); } }

        public bool ClassementProvisoire
        {
            get
            {
                if (RapportClassement == 0)
                    return false;
                var str = ClassName;
                int count = str.Length;
                if (count < 4)
                    return true;
                if (count >= 8)
                    return false;

                var tab = str.ToCharArray();
                int nb = 0;
                for (int i = 0; i < count; i++)
                {
                    if (nb > 0)
                        nb++;
                    else
                    {
                        if (tab[i] != '0')
                        {
                            nb++;
                        }
                    }
                }
                if (nb >= NB_CHAR_COMPLETE)
                    return false;

                return true;

            }
        }

        private class PieceEnumerator : IEnumerator<Piece>
        {
            private IEnumerator<Piece> childEnumerator;
            private Piece current;
            private Piece piece;

            public PieceEnumerator(Piece piece) { this.piece = piece; }
            public Piece Current
            {
                get { return current; }
            }

            public void Dispose()
            {
                if (childEnumerator != null)
                    childEnumerator.Dispose();
            }

            object System.Collections.IEnumerator.Current
            {
                get { return current; }
            }

            public bool MoveNext()
            {
                if (childEnumerator == null)
                {
                    childEnumerator = piece.childs.GetEnumerator();
                    current = piece;
                    return true;
                }
                var ret = childEnumerator.MoveNext();
                if (!ret)
                    return false;
                current = childEnumerator.Current;
                return true;
            }

            public void Reset()
            {
                if (childEnumerator != null)
                    childEnumerator.Reset();
                childEnumerator = null;
            }
        }


        public IEnumerator<Piece> GetEnumerator()
        {
            return new PieceEnumerator(this);
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return new PieceEnumerator(this);
        }

        public List<DateTime> DatesClassementBrut
        {
            get
            {
                return track.DatesClassementBrut;
            }
        }
    }




}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.gen.stat
{
    class ClassStruct
    {
        const int SIX = 6;
        const int NB = 12; 
        private int[] tab = new int[NB];
        private int level;


        private HashSet<decimal> occupés = new HashSet<decimal>(); 
        public List<int> getValues()
        {
            var list = new List<int>(); 
            for(int i=0; i<level && i< NB; i++)
                list.Add(tab[i]); 
            return list; 

        }
        public ClassStruct()
        {
            occupés.Add(0);
        }



        public void init(int level)
        {
            this.level = level;
        }

        public void position(int newLevel)
        {
            // voir si entamé
            //level 4 1.324.5 ==> level 2 1.4
            this.level = newLevel; 
            bool hasMoved = false; 
            for (int i = newLevel-1; i < NB ; i++)
            {
                if (tab[i] > 0)
                {
                    tab[i] = 0; 
                    hasMoved = true; 
                }
            }

            if (hasMoved)
            {
                //level 4 1.324.5 ==> level 2 1.4
                increment(newLevel - 1);
            }
            //else
            //    increment(newLevel); 

            //if (this.level != newLevel)
            //{
            //    int toto = 0; 
            //}


        }

        public bool isOccupe(decimal base10)
        {
            return occupés.Contains(base10); 
        }
        public bool isOccupe()
        {
            return occupés.Contains(getValueBase10());
        }

        public bool occupe(decimal base10)
        {
            return occupés.Add(base10); 
        }

        public void move()
        {
           increment(this.level);
        }



        public decimal getValue()
        {
            decimal k = 1m; 
            decimal ret = 0m;
            for (int i = 0; i < level; i++)
            {
                ret += k * tab[i];
                k /= SIX; 
            }
            return ret; 
        }

        public decimal getValueBase10()
        {
            decimal k = 1m;
            decimal ret = 0m;
            for (int i = 0; i < level; i++)
            {
                ret += k * tab[i];
                k /= 10;
            }
            return ret;
        }

        public decimal getNextValue(int level)
        {
            decimal k = 1;
            decimal ret = 0;
            for (int i = 0; i < level && i<NB; i++)
            {
                var v = tab[i];
                if (i == level - 1)
                    v++; 
                ret += k * v;
                k /= SIX;
            }
            return ret; 
        }

        private void increment(int l)
        {
            int index = l - 1;
            if (index < 0)
                return;


            int v = tab[index];
            v++;
            if (v < SIX)
            {
                tab[index] = v;
                //if (occupés.Contains(getValueBase10()))
                //    increment(l); 
            }
            else
            {
                tab[index] = 0;
                increment(l - 1);
            }


        }

        public override string ToString()
        {
            StringBuilder sb = new StringBuilder();
            sb.Append("level:");
            sb.Append(level);
            for (int i = 0; i < level && i< NB; i++)
            {
                sb.Append(" ");
                sb.Append(tab[i]); 
            }

            sb.Append(" reste:");
            for (int i = level; i < NB; i++)
            {
                sb.Append(" ");
                sb.Append(tab[i]);
            }

            return sb.ToString(); 
        }
    }
}

