                            var f = XMLTool.getAttValue(xxsub, "f");
                            if (!string.IsNullOrEmpty(f))
                            {
                                if (!f.EndsWith("\\"))
                                    f = f + "\\";
                                folders.Add(f);
                            }
                        }
                    }
                }
            }

            started = true;
            return xroot;

        }

