        {
            get
            {
                if (noteBuilder != null)
                    return noteBuilder;
                if (parent == null)
                {

                    foreach (Note _note in Conf.Notes)
                    {
                        if (_note.dir == name)
                        {
                            this.noteBuilder = new NoteBuilder(_note);
                            break;
                        }
                    }

                    if (noteBuilder != null)
                        return noteBuilder;
                    return null;
                }


                return parent.NoteBuilder;
            }
        }


