    public class ListWriterMgr
    {
        // private Thread th;
        // private pdb.util.Queue<WriterTask> queue;
        // private bool m_bEnd = false;

        private IListWriter writer;
        private BackGroundTaskMgr mgr;
        public static bool Async = true;

        public ListWriterMgr(IListWriter writer)
        {
            this.writer = writer; // writer.Clone() as IListWriter;
            if (Async)
                this.mgr = BackGroundTaskMgr.getMgrlarge("Writer " + writer.Type);


        }

        //  public bool WithOrder { set { writer.WithOrder = value; } }


        public void writePlayList(string folder, string name, List<IFile> list, bool withOrder)
        {

            var task = new playListTask(folder, name, list, withOrder, this.writer);
            if (mgr != null && Async)
                mgr.add(task);
            else
                task.exec(); 
            //lock (this)
            //{
            //    IListWriter w = this.writer; // as IListWriter;
            //    w.WithOrder = withOrder;

            //    mgr.add(new Bgtask(() =>
            //    {
            //        new WriterTask(folder, name, list, w).execute();

            //    },
            //    "{0} {1} {2} {3}", folder, name, list.Count, w));
            //}

        }

        private class playListTask : BgTaskBase
        {
            public readonly string folder;
            public readonly string name;
            public ICollection<IFile> list;
            public readonly bool withOrder;
            public readonly IListWriter writer;

            public playListTask(string folder, string name, ICollection<IFile> list, bool withOrder, IListWriter writer)
                : base(string.Format("{0} {1} {2} {3} {4} ", folder, name, list.Count, withOrder, writer.Type))
            {
                this.folder = folder;
                this.name = name;
                this.list = list;
                this.withOrder = withOrder;
                this.writer = writer;

            }

            public override void exec()
            {
                try
                {
                    writer.writePlayList(folder, name, list, withOrder, this);
                    success = true;
                }

                catch (Exception e)
                {
                    misc.log("name {0} {1} {2}", writer.Type, name, e.ToString());
                }
            }
        }


    }
