    class VirtualAlbumPiece : VirtualPiece
    {
        /// <summary>
        /// Nom servant de clé pour le groupement (artiste, chemin, ...)
        /// </summary>
        private string _group;
        /// <summary>
        /// TrackNumber virtuel
        /// </summary>
        private int _trackNumber;
        /// <summary>
        /// Type d'album virtuel
        /// </summary>
        private virtualMode mode;
        /// <summary>
        /// 
        /// </summary>
       // public override bool Dirty { get { return _dirty; } set { _dirty = value; } }

      //  private static Dictionary<string, VirtualAlbumPiece> dict = new Dictionary<string, VirtualAlbumPiece>();

        //public static VirtualAlbumPiece getVirtual(ITrackNativeDates track, Piece _real, string group, virtualMode mode, string key)
        //{
        //    //var _key = string.Format("{0}||{1}", key, _real.PieceId);
        //    //if (dict.ContainsKey(_key))
        //    //    return dict[_key];
        //    var v = new VirtualAlbumPiece(track, _real, group, mode);
        //    //dict.Add(_key, v);
        //    return v; 
        //}

        public static VirtualAlbumPiece getVirtual(ITrackNativeDates track, Piece _real, string group, virtualMode mode)
        {
            //var _key = string.Format("{0}||{1}", key, _real.PieceId);
            //if (dict.ContainsKey(_key))
            //    return dict[_key];
            var v = new VirtualAlbumPiece(track, _real, group, mode);
            //dict.Add(_key, v);
            return v;
        }


        private VirtualAlbumPiece(ITrackNativeDates track, Piece _real, string group, virtualMode mode)
            : base(track, _real)
        {
            this.mode = mode;
            this._group = group;
        }
        public override string Album
        {
            get
            {
                return _group;
            }
            set
            {

            }
        }

        public override int TrackNumber
        {
            get
            {
                return _trackNumber;
            }
            set
            {
                _trackNumber = value;
            }
        }

        public bool VirtualArtist
        {
            get
            {
                return mode == virtualMode.artist;
            }
        }
        public override virtualMode VirtualMode
        {
            get
            {
                return mode;
            }
        }

        public override string ToString()
        {
            return string.Format("{0} {1} {2}", mode, _group, base.ToString());
        }
    }
}

