using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;

namespace pdb.gen.stat
{
    public class DynamicStat : IEnumerable<DynamicStatLigne>
    {

        public const int ROWS = 5;
        public readonly List<DynamicStatLigne> rows = new List<DynamicStatLigne>(ROWS);
        public string className;
        public DynamicStat()
        {
            for (int r = 0; r < ROWS; r++)
                rows.Add(new DynamicStatLigne(r));
        }

        public DynamicStatLigne this[int row]
        {
            get
            {
                return rows[row];
            }
        }


        public IEnumerator<DynamicStatLigne> GetEnumerator()
        {
            return rows.GetEnumerator();
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return rows.GetEnumerator();
        }

        public override string ToString()
        {
            var sb = new StringBuilder();
            sb.Append(className);
            sb.Append(": ");
            foreach (var stat in rows)
            {
                sb.Append("\t");
                sb.Append(stat.ToString());
                sb.AppendLine();
            }
            return sb.ToString();


        }
    }

    public class DynamicStatLigne : IEnumerable<int>
    {
        public DynamicStatLigne(int row)
        {
            this.row = row;
            for (int i = 0; i < COLS; i++)
            {
                data.Add(0);
            }
        }
        public readonly int row;
        public const int COLS = 13;
        public const int SEMI_COLS = 6;
        private List<int> data = new List<int>(COLS);

        public int min;
        public int max;

        public int this[int col]
        {
            get
            {
                return data[Col(col)];
            }
            set
            {
                data[Col(col)] = value;
            }

        }

        private int Col(int col) { return col + SEMI_COLS; }

        public IEnumerator<int> GetEnumerator()
        {
            return data.GetEnumerator();
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return data.GetEnumerator();
        }

        public override string ToString()
        {
            StringBuilder sb = new StringBuilder();
            sb.Append(min);
            sb.Append("-");
            sb.Append(max);
            sb.Append(" ");
            for (int i = -SEMI_COLS; i <= SEMI_COLS; i++)
            {
                sb.Append(" ");
                sb.Append(i);
                sb.Append("=");
                sb.Append(this[i]);
            }
            return sb.ToString();
        }
    }

    class DynamicStatBuilder
    {
        const decimal EPSILON = 0.0000000000001m;
        private Regex reg = new Regex("[6789]");
        static NumberFormatInfo nfi;
        private List<Piece> list;
        private int level;
        //private int sub;
        //private int delta;
        //private int coeff;
        private int count;

        static DynamicStatBuilder()
        {
            nfi = new NumberFormatInfo() { NumberDecimalSeparator = "." };
        }

        public DynamicStatBuilder(IEnumerable<Piece> list)
        {
            this.list = new List<Piece>(list);
            this.list.Reverse();
            this.count = this.list.Count;
        }

        public DynamicStat gener(string className)
        {
            //if (string.IsNullOrEmpty(className))
            //    className = "3";
            DynamicStat stats = new DynamicStat();

            int pt = className.IndexOf('.');
            string classNameAndRating = className;
            var sub = classNameAndRating;
            char csub = '0';
            if (pt > 0)
            {
                int count = classNameAndRating.Length;
                //if (pt < count - 1)
                //{
                //    csub = classNameAndRating[pt + 1];
                //}
                classNameAndRating = classNameAndRating.Remove(pt);
            }

            if (pt < 0)
            {
                sub = classNameAndRating + csub;
            }
            else
            {
                sub = classNameAndRating;
                if (classNameAndRating.Length > 0)
                    classNameAndRating = classNameAndRating.Remove(classNameAndRating.Length - 1);
            }

            gener(stats[0], classNameAndRating, 1);
            gener(stats[1], sub, 3);
            //  gener(stats[2], sub, 2);
            gener(stats[2], sub, 1);

            sub += "0";

            gener(stats[3], sub, 3);
            //  gener(stats[5], sub, 2);
            gener(stats[4], sub, 1);




            return stats;
        }

        public List<Piece> getPieces(string className)
        {
            if (string.IsNullOrEmpty(className))
                className = "3";

            var stats = new List<Piece>();
            int pt = className.IndexOf('.');
            string classNameAndRating = className;
            //  var sub = classNameAndRating;
            //  char csub = '0';
            if (pt > 0)
            {
                classNameAndRating = classNameAndRating.Remove(pt);
            }

            //if (pt < 0)
            //{
            //    sub = classNameAndRating + csub;
            //}
            //else
            //{
            //    sub = classNameAndRating;
            //    if (classNameAndRating.Length > 0)
            //        classNameAndRating = classNameAndRating.Remove(classNameAndRating.Length - 1);
            //}

            gener(stats, classNameAndRating, 1);

            return stats;
        }


        private void gener(DynamicStatLigne ligne, string className, int sub)
        {


            decimal center = 0;
            //if (string.IsNullOrEmpty(className))
            //{
            //    className = "3";
            //}

            //string classNameAndRating = className.Replace(".", "").Replace(",", "");
            level = 0;
            if (string.IsNullOrEmpty(className))
                center = 3;
            else
            {
                level = className.Length - 1;
                string strDecimal = className.Insert(1, ",");


                //if (level == 0)
                //{
                //    center = 3;
                //}
                //else
                //{

                if (!decimal.TryParse(strDecimal, out center))
                    return;
                //}

            }

            decimal precision = 1m;
            long deltaBase = 1;

            for (int i = 0; i < level; i++)
            {
                precision /= 10m;
                deltaBase *= 10;
            }

            if (sub == 6)
            {
                precision *= 10m;

            }
            else
            {
                precision *= sub;

            }

            //************************************************
            // 213 =>  210  211  212  |213| 214  215  220
            // puis     2113 2120 2123 |2130|2133 2140 2143   
            // puis    2120 2122 2124 |2130|2132 2134 2140
            // puis    2123 2124 2125 |2130|2131 2132 2133
            //****************************************************

            // ok mais avec ""
            //************************************************
            // "" =>  0  1  2  |3| 4  5 
            // puis     2113 2120 2123 |2130|2133 2140 2143   
            // puis    2120 2122 2124 |2130|2132 2134 2140
            // puis    00 01 02 |03|04 05 10
            //****************************************************
            int delta = sub;
            long min = (long)System.Math.Round(System.Math.Round(center, level) * deltaBase, 0);
            long max = 0;
            int col = 0;
            int index = 0;
            int nb = 0;
            decimal current = 0m;

            if (min > 0)
            {
                for (col = -1; col >= -DynamicStatLigne.SEMI_COLS; col--)
                {
                    // trouver min en descendant
                    for (int i = 0; i < sub; i++)
                    {
                        min--;
                        while (reg.IsMatch(min.ToString()))
                            min--;
                    }

                    if (min <= 0)
                        break;
                }
            }

            if (col < -DynamicStatLigne.SEMI_COLS)
                col = -DynamicStatLigne.SEMI_COLS;
            for (; col <= DynamicStatLigne.SEMI_COLS; col++)
            {
                max = min + delta;
                while (reg.IsMatch(max.ToString()))
                    max++;

                if (min >= 6 * deltaBase)
                    return;



                decimal dmin = (decimal)min / deltaBase;

                while (true)
                {

                    if (index < count)
                    {
                        current = list[index].ClassementValueDecimal(false);
                        if (current * deltaBase < min - EPSILON)
                        {
                            index++;
                            continue;
                        }
                    }
                    if (index >= count || current * deltaBase + EPSILON >= max)
                    {
                        ligne[col] = nb;
                        nb = 0;
                        min = max;
                        break;

                    }
                    index++;
                    nb++;
                }
            }





        }




        private void gener(List<Piece> ligne, string className, int sub)
        {
            decimal center = 0;
            if (string.IsNullOrEmpty(className))
            {
                className = "3";
            }


            level = className.Length - 1;
            string strDecimal = className.Insert(1, ",");


            if (!decimal.TryParse(strDecimal, out center))
                return;



            decimal precision = 1m;
            long deltaBase = 1;

            for (int i = 0; i < level; i++)
            {
                precision /= 10m;
                deltaBase *= 10;
            }

            if (sub == 6)
            {
                precision *= 10m;

            }
            else
            {
                precision *= sub;

            }


            long delta = sub;
            long min = (long)System.Math.Round(System.Math.Round(center, level) * deltaBase, 0);
            long max = 0;
            //  int col = 0;
            int index = 0;
            //  int nb = 0;
            decimal current = 0m;

            //if (min > 0)
            //{
            //    for (col = -1; col >= -DynamicStatLigne.SEMI_COLS; col--)
            //    {
            //        // trouver min en descendant
            //        for (int i = 0; i < sub; i++)
            //        {
            //            min--;
            //            while (reg.IsMatch(min.ToString()))
            //                min--;
            //        }

            //        if (min <= 0)
            //            break;
            //    }
            //}

            //if (col < -DynamicStatLigne.SEMI_COLS)
            //    col = -DynamicStatLigne.SEMI_COLS;
            //for (; col <= DynamicStatLigne.SEMI_COLS; col++)
            //{
            max = min + delta;
            while (reg.IsMatch(max.ToString()))
                max++;

            if (min >= 6 * deltaBase)
                return;



            decimal dmin = (decimal)min / deltaBase;

            while (true)
            {
                Piece p = null;
                if (index < count)
                {
                    p = list[index];
                    current = p.ClassementValueDecimal(false);
                    if (current * deltaBase < min - EPSILON)
                    {
                        index++;
                        continue;
                    }
                }
                if (index >= count || current * deltaBase + EPSILON >= max)
                {
                    //  ligne[col] = nb;
                    //  nb = 0;
                    min = max;
                    break;

                }
                ligne.Add(p);
                index++;

            }
        }

    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.gen.stat
{
    public class NewClassement
    {
        public readonly Piece Piece;
        //1.01 => 1010
        //1.0112=> 1011 et 40
        // private int level;
        const int MULT = 1;
        //private int depth;
        string classList;
        int rating;

        public NewClassement(Piece piece)
        {
            this.Piece = piece;
        }

        public NewClassement()
        {
        }

        public void setLevel(IEnumerable<int> tab)
        {

            StringBuilder sb = new StringBuilder();
            int current = -1;
            foreach (int v in tab)
            {
                if (current >= 0)
                    sb.Append(current);
                current = v; 
            }
            classList = sb.ToString();

            if (current >= 0)
                rating = current; 
        }



        public void setLevel(int l, int depth)
        {

            // 1234.2 ==> 1 234 200 => 12342
            // 234200 (0.2342) => 02342


            string s = l.ToString();
            var sb = new StringBuilder();
            var nb = 0;

            int k = 1000 * 1000;

            while (k > 1)
            {
                if (nb >= depth)
                    break;
                if (l < k)
                {
                    sb.Append("0");
                    nb++;
                }
                else
                    break;
                k /= 10;
            }

            var chars = s.ToCharArray();
            int i=0; 
            for (i = 0; i < chars.GetLength(0); i++)
            {
                if (nb >= depth)
                    break;
                sb.Append(chars[i]);
                nb++;

            }

            classList = sb.ToString();

            if (i < chars.GetLength(0))
                rating = Convert.ToInt32(chars[i].ToString()); 
         //   rating = l - Convert.ToInt32(classList);


        }
        public string ClassList
        {
            get
            {
                return classList;
                //int l = (int)(level * MULT);
                //var sb = new StringBuilder();
                //for (int i = 0; i < depth; i++)
                //    sb.Append("0");
                //string s = l.ToString(sb.ToString());
                //return s;
            }
        }

        public int Rating
        {
            get
            {
                return rating * 20;
                //double l = level * MULT;
                //int ll = (int)(level * MULT);
                //double r = (l - ll) * 10;
                //return Convert.ToInt32(r * 20);
            }
        }

        public string getClassement()
        {
            return string.Format("{0}.{1}", classList, rating); 
        }

        public override string ToString()
        {
            if (Piece != null)
                return Piece.Location + " old: " + Piece.ClassName + ":" + Piece.Rating + ", new:" + ClassList + ":" + Rating;

            return ClassList + ":" + Rating;
        }


    }
}

