                                }
                            }
                            var listAux = new List<Piece>();
                            foreach (var p in newElements3)
                            {
                                listAux.Add(p as Piece);
                            }
                            listAux.Sort((x, y) => x.ClassementValueLightSt.CompareTo(y.ClassementValueLightSt));

