        public void load()
        {
            lock (_lock)
            {
                try
                {
                    var strVersion = "1";
                    TrackAlbum currentAlbum = null;
                    TrackInfoItunes currentTrack = null;
                    List<DateTime> currentList = null;
                    // string currentAlbumName = null;
                    using (XmlReader reader = XmlReader.Create(FILE))
                    {
                        while (reader.Read())
                        {
                            if (reader.NodeType == XmlNodeType.Element)
                            {
                                if (reader.Name == "db")
                                {
                                    var seq = reader.GetAttribute(SEQ_PIECE);
                                    pieceSequenceId = Convert.ToInt32(seq);
                                    strVersion = reader.GetAttribute(TOKEN_VERSION);
                                }
                                else if (reader.Name == "a")
                                {
                                    currentAlbum = readAlbum(reader);
                                    // currentAlbumName = currentAlbum.Album.toKey();
                                    //if (dictAlbum.ContainsKey(currentAlbumName))
                                    //{
                                    //    currentAlbum = dictAlbum[currentAlbumName];
                                    //}
                                    //else
                                    //    dictAlbum.Add(currentAlbumName, currentAlbum);
                                }
                                else if (reader.Name == "t")
                                {
                                    if (currentTrack != null && currentList != null)
                                    {
                                        currentTrack.Dates = currentList;
                                        currentList = null;
                                    }
                                    currentTrack = read(reader, currentAlbum);
                                    dict[currentTrack.ShortName] = currentTrack;
                                    if (!string.IsNullOrEmpty(currentTrack.Url))
                                        dictUrl[currentTrack.Url] = currentTrack;
                                    dictId[currentTrack.DbId] = currentTrack;
                                }
                                else if (reader.Name == "dates")
                                {
                                    currentList = new List<DateTime>();
                                    currentList.Add(currentTrack.PlayedDate);
                                }

                                else if (reader.Name == "d")
                                {
                                    currentList.Add(Convert.ToDateTime(reader.ReadInnerXml()));
                                }

                                else if (reader.Name == "u")
                                {
                                    currentAlbum.Urls.Add(reader.GetAttribute("url"));
                                }
                            }


                        }

                        if (currentTrack != null && currentList != null)
                        {
                            currentTrack.Dates = currentList;
                            currentList = null;
                        }

                    }

                    using (XmlReader reader = XmlReader.Create(FILE_STAT))
                    {
                        while (reader.Read())
                        {
                            if (reader.NodeType == XmlNodeType.Element)
                            {
                                if (reader.Name == "db")
                                {

                                }
                                else
                                {
                                    int id = Convert.ToInt32(reader.GetAttribute("id"));


                                    var t = dictId[id];
                                    if (t == null)
                                    {
                                        string loc = reader.GetAttribute("f");
                                        Program.log.log("track {0} dans les stats non vu dans db {1}", id, loc);
                                    }
                                    else
                                    {
                                        t.readStat(reader);
                                        t.readStatGlobal(reader);
                                    }
                                }
                            }
                        }

                    }


                    foreach (string source in sources)
                    {
                        try
                        {
                            using (XmlReader reader = XmlReader.Create(source))
                            {
                                while (reader.Read())
                                {
                                    if (reader.NodeType == XmlNodeType.Element)
                                    {
                                        if (reader.Name == "db")
                                        {

                                        }
                                        else
                                        {
                                            int id = Convert.ToInt32(reader.GetAttribute("id"));


                                            var t = dictId[id];
                                            if (t == null)
                                            {

                                            }
                                            else
                                            {
                                                t.readStatGlobal(reader);
                                            }
                                        }
                                    }
                                }

                            }
                        }

                        catch (Exception ex)
                        {
                            misc.log(ex);
                        }
                    }


                    //using (XmlReader reader = XmlReader.Create(GLOBAL_STAT))
                    //{
                    //    while (reader.Read())
                    //    {
                    //        if (reader.NodeType == XmlNodeType.Element)
                    //        {
                    //            if (reader.Name == "db")
                    //            {

                    //            }
                    //            else
                    //            {
                    //                int id = Convert.ToInt32(reader.GetAttribute("id"));


                    //                var t = dictId[id];
                    //                if (t == null)
                    //                {
                    //                    string loc = reader.GetAttribute("f");
                    //                    Program.log.log("track {0} dans les global stats non vu dans db {1}", id, loc);
                    //                }
                    //                else
                    //                {
                    //                    t.readStatGlobal(reader);
                    //                    t.consolidStat();
                    //                }
                    //            }
                    //        }
                    //    }

                    //}

                    foreach (TrackInfoItunes t in dictId.Values)
                    {
                        if (t.ParentId > 0)
                            t.Parent = dictId[t.ParentId];
                    }

                }

                finally
                {
                    loaded = true;
                }
            }
        }


