using System.Collections.Generic;
using System.Xml;
using pdb.util;
using System;

namespace pdb.podcast.Tuning
{
    class Contexte :TuningBase
    {
        public int nb = -1;
        public int nbMax = -1;

        public List<string> debuts = new List<string>();
        public List<string> fins = new List<string>();


        public Contexte() : base() { }
        public Contexte(XmlElement node) : base(node)
        {
            if (node == null)
                return;

            buildi("nb", v => nb = v);
            buildi("nbMax", v => nbMax = v); 
            foreach (XmlNode sub in node.ChildNodes)
            {
                if (sub is XmlElement)
                {
                    var a = sub as XmlElement;
                    string name = a.GetAttribute("name");
                    if (sub.Name == "d")
                        debuts.Add(name);
                    else
                        fins.Add(name); 
                }
            }
        }


    }
}
