using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Windows;
using System.Windows.Media;

namespace pdb.player.Vue.Util
{
    internal static class Util<T> where T : DependencyObject
    {
        internal static T FindAnchestor(DependencyObject current)
        {
            do
            {
                if (current is T)
                {
                    return (T)current;
                }
                current = VisualTreeHelper.GetParent(current);
            }
            while (current != null);
            return null;
        }
    }
}
