using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.gen.albums
{
   public class PieceExtended
    {
        private Piece piece;
        private PieceSupInfo main;
        private PieceSupInfo min;


        public PieceExtended(Piece piece)
        {
            this.piece = piece;
            var cl = piece.RapportClassement;
            this.main = new PieceSupInfo(cl);
            this.min = new PieceSupInfo(cl);

        }

        public decimal VirtualClassement { get { return main.Value; } }
        public decimal VirtualClassement2 { get { return min.Value; } }

        public bool getCauseUp(bool? natureCause)
        {
            if (true == natureCause)
                return min.Coeff > 1;
            if (false == natureCause)
                return min.Coeff > 1;
            return min.Coeff > 1 || main.Coeff > 1; 
        }

        public decimal CoeffVirtuel { get { return main.Coeff; } }
        public decimal CoeffVirtuel2 { get { return min.Coeff; } }
        public decimal getVirtual(bool main)
        {
            if (main)
                return this.main.Value;
            else
                return this.min.Value;
        }
        public Piece Piece { get { return piece; } }
        public bool Enabled { get { return piece.Enabled; } }

        public bool LastClassementRecent { get { return piece.LastClassementRecent; } }
        public decimal RapportClassement { get { return piece.RapportClassement; } }


        public void setCoeff(decimal coeff, bool main)
        {
            if (main)
                this.main.Coeff = coeff;
            else
                this.min.Coeff = coeff;
        }

        public void setValue(decimal value, bool main)
        {
            if (main)
                this.main.Value = value;
            else
                this.min.Value = value;
        }

        public void finalyse()
        {
            this.piece.setClassementVirtuel(this.main.Value);
            this.piece.setClassementVirtuel2(this.min.Value); 
        }

    }

    class PieceSupInfo
    {


        private decimal value0;
        private decimal value;

        public PieceSupInfo(decimal value)
        {
            this.value = value;
             this.value0 = value;
        }

        public decimal Coeff { get { if (value0 <= 0) return 1m; return value / value0; } set { this.value *= value; } }

        public decimal Value { get { return value; } set { this.value = value; } }
    }
}

using System;
using pdb.gen.medianne;
using pdb.util;
using System.Xml;
using pdb.gen.auto;

namespace pdb.gen.Tuning
{
    [Serializable]
    public class ConfMedianne : TuningBase
    {
        public medianneMode type;
        public decimal medianne = 0.5m;
        public bool quasi;

        public bool enabled;



        public bool removeFirst;
        public bool deleteFirst;
        public removeLast removeLast;

        public decimal expEnabled = 0m;

        public decimal zero = 0m;
        /// <summary>
        /// 
        /// </summary>
        public bool removeEnabled;
        /// <summary>
        /// cumule l'influence des deux parties main et min
        /// </summary>
        public bool combine;
        /// <summary>
        /// calcul simple des selectedMotif
        /// </summary>
        public bool simple;



        public modeMin cmpMin;

        private ConfMedianneVirt _main;
        private ConfMedianneVirt _min;

        public ConfMedianneVirt Main { get { return _main; } }
        public ConfMedianneVirt Min { get { return _min; } }
        public ConfMedianne()
        {
            _main = new ConfMedianneVirt();
            _min = new ConfMedianneVirt();

        }

        public void makeIdem()
        {
            _min = _main;
        }

        public void duplicateMainIf()
        {
            if (_min == _main)
                _min = new ConfMedianneVirt(_main);
        }

        public ConfMedianne(ConfMedianne o, bool raz)
        {
            type = o.type;
            medianne = o.medianne;
            quasi = o.quasi;

            enabled = o.enabled;



            removeFirst = o.removeFirst;
            deleteFirst = o.deleteFirst;
            removeLast = o.removeLast;

            expEnabled = o.expEnabled;

            zero = o.zero;
            removeEnabled = o.removeEnabled;



            cmpMin = o.cmpMin;

            _main = new ConfMedianneVirt(o._main, raz);
            _min = new ConfMedianneVirt(o._min, raz);

            this.simple = o.simple;
            this.combine = o.combine; 

        }

        public ConfMedianne(XmlElement parent, XmlElement child)
            : base(parent, child)
        {
            var strType = "";
            builds("type", v => strType = v);
            type = (medianneMode)Enum.Parse(typeof(medianneMode), strType, true);
            builddec("medianne", v => medianne = v);
            buildb("quasi", v => quasi = v);
            buildb("enabled", v => enabled = v);
            buildb("removeFirst", v => removeFirst = v);
            buildb("deleteFirst", v => deleteFirst = v);
            builddec("expEnabled", v => expEnabled = v);
            builddec("zero", v => zero = v);
            buildb("removeEnabled", v => removeEnabled = v);
            string _modeMin = "";
            builds("cmpMin", v => _modeMin = v);
            if (!string.IsNullOrEmpty(_modeMin))
                cmpMin = (modeMin)Enum.Parse(typeof(modeMin), _modeMin, true);

            string _removeLast = null;
            builds("removeLast", v => _removeLast = v);
            removeLast = (removeLast)Enum.Parse(typeof(removeLast), _removeLast, true);

            buildb("combine", v => combine = v);
            buildb("simple", v => simple = v);

            _main = null;
            _min = null;
            var subs = XMLTool.NodeLookUp(child, "virts");
            if (subs != null)
            {
                foreach (XmlNode _n in subs.ChildNodes)
                {
                    if (_n is XmlElement)
                    {
                        if (_main == null)
                        {
                            _main = new ConfMedianneVirt(child, _n as XmlElement);

                        }
                        else if (_min == null)
                        {
                            _min = new ConfMedianneVirt(child, _n as XmlElement);
                        }
                    }
                }
                if (_min == null && _main != null)
                    _min = new ConfMedianneVirt(_main);
            }
        }

        public override void save(XmlWriter w)
        {
            base.save(w);
            writeStart("cmp");
            write("type", type);
            write("medianne", medianne);
            write("quasi", quasi);
            write("enabled", enabled);
            write("removeFirst", removeFirst);
            write("deleteFirst", deleteFirst);
            write("expEnabled", expEnabled);
            write("zero", zero);
            write("removeEnabled", removeEnabled);
            write("cmpMin", cmpMin);
            write("removeLast", removeLast);
            write("combine", combine);
            write("simple", simple); 

            writeStart("virts");
            _main.save(w);
            _min.save(w);
            writeEnd();

            writeEnd();



        }


