        public static void setHumanSelected(PlayListViewModel pl, TrackListViewModel track)
        {
            if (track != humanSelected)
            {
                humanSelected = track;
                  if (CurrentHumanSelectedChanged != null)
                      CurrentHumanSelectedChanged(pl, EventArgs.Empty);
            }
        }

