        public ConfMedianneW(ConfMedianne conf, IConfSort parent)
           :this()
        {
            this.conf = conf;

            this.main = new ConfVirt(conf.Main, parent);
            this.min = new ConfVirt(conf.Min,parent);

            gmain.Children.Add(main);
            gmin.Children.Add(min);

            InitializeComponent();

        }

        public void display()
        {



            cb_enabled.IsChecked = conf.enabled;
            cbxSort.SelectedIndex = (int)conf.type;
            tb_medianne.Text = conf.medianne.ToString();
            cb_quasi.IsChecked = conf.quasi;

            cb_removeFirst.IsChecked = conf.removeFirst;
            cb_deleteFirst.IsChecked = conf.deleteFirst;
            cbx1_removeLast.SelectedIndex = (int)conf.removeLast;

            tb_exp.Text = conf.expEnabled.ToString();
            tb_zero.Text = Piece.getClassementEquiv(conf.zero);
            cb_removeEnabled.IsChecked = conf.removeEnabled;

            cbx_cmpMinn.SelectedIndex = (int)conf.cmpMin;

            main.display();
            min.display(); 

        }

        public void record()
        {

            conf.enabled = cb_enabled.IsChecked.Value;
            conf.type = (util.medianneMode)cbxSort.SelectedIndex;
            conf.medianne = Convert.ToDecimal(tb_medianne.Text);
            conf.quasi = cb_quasi.IsChecked.Value;

            conf.removeFirst = cb_removeFirst.IsChecked.Value;
            conf.deleteFirst = cb_deleteFirst.IsChecked.Value;
            conf.removeLast = (util.removeLast)cbx1_removeLast.SelectedIndex;

            conf.expEnabled = Convert.ToDecimal(tb_exp.Text);
            var cl = Classement.create(tb_zero.Text);
            conf.zero = cl.RapportClassement;
            conf.removeEnabled = cb_removeEnabled.IsChecked.Value;

            conf.cmpMin = (util.modeMin)cbx_cmpMinn.SelectedIndex;

            main.record();
            min.record(); 

        }

        public void onchange()
        {
            main.onchange();
            min.onchange(); 
        }
    }
}

