using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.podcast.Notes
{
    class ListNote
    {
        private int note;
        public ListNote(int note)
        {
            this.note = note; 
        }
        private HashSet<int> hs = new HashSet<int>();
        private List<TrackInfoItunes> list = new List<TrackInfoItunes>();

        public void Add(TrackInfoItunes t)
        {
            if (hs.Add(t.DbId))
                list.Add(t); 
        }
        public int Note { get { return note; } }
        public List<TrackInfoItunes> List { get { return list; } }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.podcast.Tuning;

namespace pdb.podcast.Notes
{
    class NoteBuilder
    {
        private ListNote[] depot;
        private Note conf;
        private List<TrackSelector> selectors = new List<TrackSelector>();
        public NoteBuilder(Note conf)
        {
            this.conf = conf;
            foreach (SelectParamBase p in conf)
                selectors.Add(new TrackSelector(p));
            depot = new ListNote[conf.max - conf.min + 1];
            for (int i = 0; i <= conf.max - conf.min; i++)
            {
                depot[i] = new ListNote(i + conf.min);
            }
        }

        public void build(List<TrackInfoItunes> list)
        {
            foreach (TrackInfoItunes t in list)
                build(t);
        }


        private void build(TrackInfoItunes t)
        {
            int note = 0;

            foreach (TrackSelector ts in selectors)
            {
                if (ts.selectSimple(t))
                    note += (ts.Param as NoteItem).v;
            }
            ListNote d = null;
            if (note < conf.min)
                note = conf.min;
            if (note > conf.max)
                note = conf.max;


            d = depot[note - conf.min];
            d.Add(t);
        }

        public IEnumerable<ListNote> Depot { get { return depot; } }
    }
}

