    public class SelectParam : SelectParamBase
    {


        public bool dateCroissante;
        public string[] depot; 



        public SelectParam(XmlElement parent, XmlElement node)
            : base(parent,node)
        {

            buildb("dateCroissante", v => dateCroissante = v);

            var strdepot = "";
            builds("depot", v => strdepot = v); 
            if (string.IsNullOrEmpty(strdepot))
                return;
            depot = strdepot.Split('|'); 
        }





    }
