        private CPiece parent;
        public CPiece Parent
        {
            get { return parent; }
            set
            {
                parent = value;
                if (parent == null)
                {
                    PieceParentId = -1;
                    return;
                }
                parent.addDataLink(this);
                PieceParentId = parent.PieceId;

            }
        }

        private bool deleted;
        public bool Deleted
        {
            get { return deleted; }
            set
            {
                if (value != deleted)
                {
                    if (value)
                        misc.log("Suppression de {0}", this); 
                    deleted = value;
                }
            }
        }
