namespace pdb.podcast.Selection
{
    public class CauseItem
    {

        public readonly LimitItemBase limit;
        public readonly borderType type;
        public readonly timeSpan val;
        public readonly byte mode;
        //public CauseItem(borderType type, bool success, timeSpan val, LimitItemBase limit,bool selected)
        //{
        //    this.mode = Conv.getMode(success, selected); 
        //    this.type = type;
        //    this.val = val;
        //    this.limit = limit;
        //}

        public CauseItem(CauseDataValue cd,LimitItemBase limit)
        {
            this.mode = (byte)cd.mode;
            this.type = cd.type;
            this.val = cd.val;
            this.limit = limit; 
        }



        public bool Ok { get { return mode.Success();  } }
        public bool Selected { get { return mode.Selected(); } }

        public override string ToString()
        {
            var str = "";
            if (!Ok)
                str = "nok";
            var strSelected = "s";
            if (!Selected)
                strSelected = "ns"; 
            return string.Format("{0} {1} {2} {3} {4}", type, str,strSelected, val, limit);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.util;

namespace pdb.podcast.Selection
{

   public class CauseRegister
    {
        private BgDictString0<Causes> dict = new BgDictString0<Causes>();
        public void Clear() { dict.Clear(); }
        public Causes Get(TrackInfoItunes candidat)
        {
            Causes causes = null;
            string loc = candidat.Location;
            if (!dict.ContainsKey(loc))
            {
                causes = new Causes(candidat);
                dict.Add(loc, causes);
                return causes;
            }
            return dict[loc];
        }
        public virtual void signal(CauseDatas cd)
        {
            var causes = Get(cd.candidat);
            causes.check(cd); 
        }

        public List<Causes> getList(bool onlyRejected)
        {
            var list = new List<Causes>(dict.Values);
            list.Sort(new CauseComparer());
            if (!onlyRejected)
                return list;
            else
            {
                var aux = new List<Causes>();

                foreach (Causes causes in list)
                {
                    if (!causes.candidat.Selected)
                        aux.Add(causes);
                }
                return aux;
            }

        }

        private class CauseComparer : IComparer<Causes>
        {
            public int Compare(Causes x, Causes y)
            {
                return x.candidat.Pub.CompareTo(y.candidat.Pub);
            }
        }
    }

    class NullRegister : CauseRegister
    {
        public override void signal(CauseDatas cd)
        {

        }
    }
}

