using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.util;

namespace pdb.podcast.Albums
{

    public class PodcastInAlbumComparer : IComparer<TrackInfoItunes>
    {
        public virtual int Compare(TrackInfoItunes x, TrackInfoItunes y)
        {
            int cmp = x.Pub.Date.CompareTo(y.Pub.Date);
            if (cmp != 0) return cmp;

            return x.Duree.Value.CompareTo(y.Duree.Value);
        }
    }

    class AlbumConsolid
    {
        private static Logger log = Logger.getLogger("doublons");
        public static int nb123, nb12, nb13, nb23, nb3n = 0;
        public static int nb1, nb2, nb3, nb0 = 0;
        private static IComparer<TrackInfoItunes> cmp = new PodcastInAlbumComparer();
        public void consolid(TrackAlbum album)
        {          
            if (album.Album == "2000 ANS D'HISTOIRE")
                return;

            var list = album.List;
            list.Sort(cmp);

            Dictionary<DateTime, List<TrackInfoItunes>> dict = new Dictionary<DateTime, List<TrackInfoItunes>>();

            List<TrackInfoItunes> dictincts = new List<TrackInfoItunes>(); 
            foreach (TrackInfoItunes t in list)
            {
                TrackInfoItunes apparente = null; 
                foreach (TrackInfoItunes last in dictincts)
                {
                    if (t.Pub.Date == t.Pub && t.Pub.DayOfYear == 1)
                        continue;
                    var dt = t.Pub.Date;

                    if (last != null && dt == last.Pub.Date && (int)t.Duree.TotalMinute == (int)last.Duree.TotalMinute)
                    {
                        if (t.Pub == DateTime.MinValue)
                            continue;
                        bool exactPub = last.Pub == t.Pub;
                        if (last.Pub.Date == last.Pub || t.Pub.Date == t.Pub)
                            exactPub = false;
                        bool exactDuree = last.Duree == t.Duree;
                        bool exactLogic = last.LogicalName.Equals(t.LogicalName, StringComparison.InvariantCultureIgnoreCase);


                        if (exactPub && exactDuree && exactLogic)
                        {
                            nb123++; apparente = last; 
                        }
                        else if (exactPub && exactDuree)
                        {
                            nb12++; apparente = last; 
                        }
                        else if (exactPub && exactLogic)
                        {
                            nb13++; apparente = last; 
                        }
                        else if (exactDuree && exactLogic)
                        {
                            if (dt.Date == last.Pub.Date)
                            {
                                nb23++; apparente = last; 
                            }
                            else
                            {
                                nb3n++;

                            }
                        }
                        else
                        {
                            if (exactPub)
                                nb1++;
                            else if (exactDuree)
                                nb2++;
                            else if (exactLogic)
                                nb3++;
                            else
                                nb0++;

                        }

                        if (apparente != null)
                        {

                            if (!dict.ContainsKey(dt))
                            {
                                dict.Add(dt, new List<TrackInfoItunes>());
                            }
                            var subList = dict[dt];
                            if (!subList.Contains(last))
                                subList.Add(last);
                            subList.Add(t);

                            break; 
                        }

                    }
                }

                if (apparente == null)
                    dictincts.Add(t); 
            }

            foreach (KeyValuePair<DateTime, List<TrackInfoItunes>> kvp in dict)
            {
                log.log(string.Format("{0} {1}", album.Album, kvp.Key.ToString()));
                foreach (TrackInfoItunes t in kvp.Value)
                {
                    log.log("{0} {1}", t.Duree.Value, t.Location);
                }
            }

            var conf = album.ConfAlbum; 


        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
