        {
            return conteneur.Tracks.Total.Use.getEmpreinte();
        }

        #region IBorder
        private List<IBorderTransmit> borderObs = new List<IBorderTransmit>();
        public void addBorderObs(IBorderTransmit auto)
        {
            borderObs.Add(auto);
        }
        #endregion
        #endregion

        public void signal(TrackBorderItem tb)
        {
            if (false == HistoAll)
                return;
            bool _checked = false;
            foreach (IBorderTransmit border in borderObs)
            {
                border.signal(tb);
                if (border.Checked)
                    _checked = true;
            }

            if (_checked)
                return;
            if (parent != null)
                parent.signal(tb);
        }




        public void signal(CauseDatas cd)
        {
            if (false == HistoAll || serial)
                return;
            bool _checked = false;
            foreach (IBorderTransmit border in borderObs)
            {
                border.signal(cd);
                if (border.Checked)
                    _checked = true;
            }

            if (_checked)
                return;
            if (parent != null)
                parent.signal(cd);
        }
    }
