using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.util
{
    public struct DecimalIndex
    {
        public decimal d;
        public int index;

        public override bool Equals(object obj)
        {
            if (obj is DecimalIndex)
            {
                var o = (DecimalIndex)obj;
                return o.d.Equals(d); 
            }
            else
                return false; 

        }

        public static bool operator == (DecimalIndex x, DecimalIndex y)
        {
            return x.d == y.d; 
        }
        public static bool operator !=(DecimalIndex x, DecimalIndex y)
        {
            return x.d != y.d;
        }
        public static bool operator >(DecimalIndex x, DecimalIndex y)
        {
            return x.d > y.d;
        }
        public static bool operator >=(DecimalIndex x, DecimalIndex y)
        {
            return x.d >= y.d;
        }
        public static bool operator <(DecimalIndex x, DecimalIndex y)
        {
            return x.d < y.d;
        }
        public static bool operator <=(DecimalIndex x, DecimalIndex y)
        {
            return x.d <= y.d;
        }

        public override string ToString()
        {
            return d.ToString();
        }
    }
}

