        private List<TrackListViewModel> SelectedTracks
        {
            get
            {
                lock (this)
                {
                    var l = new List<TrackListViewModel>();

                    foreach (TrackListViewModel t in dg.ItemsSource)
                    // foreach (TrackListViewModel t in dg.ItemsSource.Cast<TrackListViewModel>())
                    {
                        if (t.IsSelected)
                            l.Add(t);
                    }
                    return l;
                }
            }
        }
