namespace pdb.player.model
{
    class Img
    {
        public readonly long size;
        public readonly BitmapImage img;
        public Img(BitmapImage img, long size)
        {
            this.img = img;
            this.size = size;
        }
    }
    class ArtWorkManager
    {
        private Dictionary<string, SimpleLinkedList<Img>> dicttagLib = new Dictionary<string, SimpleLinkedList<Img>>();
        private Dictionary<string, SimpleLinkedList<Img>> dictDir = new Dictionary<string, SimpleLinkedList<Img>>();
        private Dictionary<int, SimpleLinkedList<BitmapImage>> dictPiece = new Dictionary<int, SimpleLinkedList<BitmapImage>>();
        private static Logger log = Logger.getLogger("Img");


        public List<BitmapImage> get(CPiece p)
        {

            var ret = new List<BitmapImage>();
            if (p == null)
                return ret ; 
            if (dictPiece.ContainsKey(p.MasterId))
            {
                var l = dictPiece[p.MasterId];
                if (l == null)
                    return ret;

                foreach (BitmapImage img in l)
                    ret.Add(img);
                return ret;
            }

            dictPiece.Add(p.MasterId, null); 
            var list = new List<Img>();


            foreach (CFile f in p.Files)
            {
                var filename = f.Path;
                if (dicttagLib.ContainsKey(filename))
                {
                    var _l = dicttagLib[filename];
                    if (_l != null)
                    {
                        foreach (var i in dicttagLib[filename])
                            list.Add(i);
                    }
                    continue;
                }

                dicttagLib.Add(filename, null);
                try
                {
                    if (f.exists())
                    {

                        var file = TagLib.File.Create(filename);

                        foreach (var pict in file.Tag.Pictures)
                        {
                            try
                            {
                                var bin = pict.Data.Data;
                                using (var stream = new MemoryStream(bin))
                                {
                                    BitmapImage i = new BitmapImage();
                                    i.BeginInit();
                                    i.StreamSource = stream;
                                    i.CacheOption = BitmapCacheOption.OnLoad;
                                    i.EndInit();
                                    i.Freeze();
                                    var size = bin.LongLength;
                                    var img = new Img(i, size);

                                    var item = dicttagLib[filename];
                                    if (item == null)
                                    {
                                        item = new SimpleLinkedList<Img>();
                                        dicttagLib[filename] = item;
                                    }

                                    item.add(img);
                                    list.Add(img); 


                                    log.log("load img from tagLib {0} {1}", filename, FileUtil.HumanReadableSize(size, -1));

                                }
                            }

                            catch (Exception ex)
                            {
                            }
                        }

                    }

                }
                catch (Exception ex)
                {
                }
            }


            foreach (CFile f in p.Files)
            {
                var dir = Path.GetDirectoryName(f.Path);

                if (dictDir.ContainsKey(dir))
                {
                    var _l = dictDir[dir];
                    if (_l != null)
                    {
                        foreach (var i in dictDir[dir])
                            list.Add(i);
                    }
                    continue;
                }

                dictDir.Add(dir, null);
                try
                {
                    if (f.exists())
                    {
                        var fdir = FileRegister.CheckDir(f.Path);
                        foreach (FileValue fimg in fdir.imgs)
                        {
                            try
                            {
                                var bin = File.ReadAllBytes(fimg.FullName);


                                using (var stream = new MemoryStream(bin))
                                {
                                    BitmapImage i = new BitmapImage();
                                    i.BeginInit();
                                    i.StreamSource = stream;
                                    i.CacheOption = BitmapCacheOption.OnLoad;
                                    i.EndInit();
                                    i.Freeze();
                                    var size = bin.LongLength;
                                    var img = new Img(i, size);

                                    var item = dictDir[dir];
                                    if (item == null)
                                    {
                                        item = new SimpleLinkedList<Img>();
                                        dictDir[dir] = item;
                                    }

                                    item.add(img);
                                    list.Add(img); 
                                    log.log("load img from src {0} {1}", fimg.FullName, FileUtil.HumanReadableSize(size, -1));

                                }
                            }

                            catch (Exception ex)
                            {
                            }
                        }
                    }



                }

                catch (Exception ex)
                {
                }

            }
            if (list.Count > 0)
            {
                list.Sort((x, y) => x.size.CompareTo(y.size));
                list.Reverse();
                var ll = new SimpleLinkedList<BitmapImage>();
                foreach (Img img in list)
                {
                    ll.add(img.img);
                    ret.Add(img.img);
                }
                dictPiece[p.MasterId] = ll;
            }
            return ret; 
        }
    }
}

