using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.util.list
{
    public class SimpleLinkedList<V> : IEnumerable<V>
    {
        private LinkedItem<V> first;
        public void add(V value)
        {
            var item = new LinkedItem<V>(value);
            if (first == null)
            {
                first = new LinkedItem<V>(value);
            }
            else
            {
                var current = first;
                while (current.next != null)
                {                    
                    current = current.next; 
                }
                current.next = item; 
            }
        }

        public void modify(V value, V newV)
        {
            LinkedItem<V> current = first;

            while (current != null)
            {
                if (value.Equals(current.value))
                {
                    current.value = newV;
                    break;
                }

                current = current.next;
            }
        }

        public void Remove(V value)
        {
            LinkedItem<V> _pre = null;
            LinkedItem<V> current = first;

            while (current != null)
            {
                if (value.Equals(current.value))
                {
                    var next = current.next;
                    if (_pre == null)
                    {
                        // on est à first                        
                        first = next;
                    }
                    else
                    {
                        _pre.next = next;
                    }



                    break;
                }
                _pre = current;
                current = current.next;
            }
        }

        public IEnumerator<V> GetEnumerator()
        {
            return new ItemEnumerator(first);
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return new ItemEnumerator(first);
        }



        public class ItemEnumerator : IEnumerator<V>
        {
            private LinkedItem<V> current;
            private LinkedItem<V> first;
            public ItemEnumerator(LinkedItem<V> first)
            {
                this.first = first;
            }
            public V Current
            {
                get { return current.value; }
            }

            public void Dispose()
            {

            }

            object System.Collections.IEnumerator.Current
            {
                get { return current.value; }
            }

            public bool MoveNext()
            {
                if (current == null)
                    current = first;
                else
                    current = current.next;
                if (current == null)
                    return false;

                return true;
            }

            public void Reset()
            {
                current = null;
            }
        }



    }


}
