namespace pdb.podcast.Tuning
{
    public class Export : TuningBase
    {
        public bool enabled;
        public string dir;
        public string fIn;
        public string fOut;

        public Export()
        {
        }

        public Export(XmlElement node)
            : base(node)
        {
            if (node == null)
                return;
            buildb("enabled", v => enabled = v);
            builds("dir", v => dir = v);
            builds("fIn", v => fIn = v);
            builds("fOut", v => fOut = v);
        }
    }

    public class Import : TuningBase
    {
        public bool enabled;
        public string dir; 
        public Import()
        {
        }

        public Import(XmlElement node)
            : base(node)
        {
            if (node == null)
                return;
            buildb("enabled", v => enabled = v);
            builds("dir", v => dir = v);
        }
    }

}

