                    med2 = AlbumMedianne.getMedianneLight(list, mode, mode.Min, false, false);
                    med1 = AlbumMedianne.getMedianneLight(list, mode, mode.Main, true, false);

                    //comparaison des médiannes


                    if (med2.d < med1.d && med2.d > 0)
                    {

                        resultGlobalMin = true;
                        med = med2;
                    }
                    else
                    {
                        resultGlobalMin = false;
                        med = med1;

