                buildVirtual0(mode.Min, false);

                if (combine)
                {
                    foreach (PieceExtended p in pieces)
                        p.Combine();
                    align(mode.Main, PieceExtended.mode.combine);
                }

                if (!simple)
                {
                    align(mode.Main, PieceExtended.mode.main);
                    align(mode.Min, PieceExtended.mode.min);
                }
            }



