                atLeastOneEnabled = false;
                int _nbr = 0;
                int _nbdone = 0;


                for (int i = 0; i < count; i++)
                {
                    var p = list[i];


                    if (!sortDone)
                        p.resetMedianneCompact();
                    if (p.Enabled)
                    {
                        atLeastOneEnabled = true;

                    }
                    if (!p.Enabled && p.ClassementValueDecimal(false) > 0)
                        _nbdone++;
                    if (p.LastClassementRecent)
                        _nbr++;
                    var pp = new PieceExtended(p);
                    listE.Add(pp);
                }

                nbRecent = _nbr;
                nbDone = _nbdone;


