    public class CListPiece : IEnumerable<RealPiece>
    {
        private List<RealPiece> m_list = new List<RealPiece>();
        private DictRef<RealPiece> ht = new DictRef<RealPiece>();
        private BgDict<int, RealPiece> htIds = new BgDict<int, RealPiece>();

        /// <summary>
        /// Création à partir de la liste native
        /// </summary>
        /// <param name="a_list"></param>
        /// <remarks></remarks>
        public CListPiece(IList<RealPiece> a_list)
        {
            foreach (RealPiece l_file in a_list)
            {
                add(l_file);
            }

            link(); 
        }

        private void link()
        {
            foreach (RealPiece piece in m_list)
            {
                int parentId = piece.PieceParentId;
                if (parentId > 0)
                {
                    piece.setParent(htIds[parentId]);
                }
            }
        }

        public bool Contains(RealPiece piece)
        {
            TrackIdentity key = piece.Key; 
            RealPiece l_pieceOrg = ht[key]; // getPiece(a_piece);

            if (l_pieceOrg == null)
            {
                return false;
            }
            return true;
        }

        /// <summary>
        /// Ajout sans doublon
        /// </summary>
        /// <param name="a_piece"></param>
        /// <remarks>TODO faire le ménage dans iTunes</remarks>
        private void add(RealPiece a_piece)
        {
            TrackIdentity key = a_piece.Key; 
            RealPiece l_pieceOrg = ht[key]; // getPiece(a_piece);

            if (l_pieceOrg == null)
            {
                m_list.Add(a_piece);
                ht.set(a_piece, key);
                htIds.Add(a_piece.PieceId, a_piece);

            }
            //else
            //{
            //    l_pieceOrg.mergeDataFrom(a_piece);
            //}

        }



        //public Piece getPiece(ITrackMetaData a_piece)
        //{
        //    TrackIdentity key = new TrackIdentity(a_piece);
        //    return ht[key]; 
        //    //foreach (Piece l_file in m_list)
        //    //{
        //    //    if (l_file.checkIdentity(a_piece))
        //    //    {
        //    //        return l_file;
        //    //    }
        //    //}
        //   // return null;
        //}


        public int Count
        {
            get { return m_list.Count; }
        }

        public RealPiece this[int a_index]
        {
            get
            {
                if (a_index < 0 || a_index >= Count)
                {
                    throw new ApplicationException("bad index:" + a_index + " / " + Count);
                }
                else
                {
                    return m_list[a_index];
                }
            }
        }

        public IEnumerator<RealPiece> GetEnumerator()
        {
            return m_list.GetEnumerator();
        }


        #region IEnumerable Membres

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return m_list.GetEnumerator();
        }

        #endregion
    }
}

