        {
            get
            {
                return track.MasterPiece;
            }
        }


        public virtual HashSet<Piece> Childs { get { return new HashSet<Piece>(); } }

        private class PieceEnumerator : IEnumerator<Piece>
        {
            private Piece current;
            private Piece piece;

            public PieceEnumerator(Piece piece) { this.piece = piece; }
            public Piece Current
            {
                get { return current; }
            }

            public void Dispose()
            {

            }

            object System.Collections.IEnumerator.Current
            {
                get { return current; }
            }

            public bool MoveNext()
            {
                if (current == null)
                {
                    current = piece;
                    return true;
                }
                return false;
            }

            public void Reset()
            {

            }
        }



        public virtual IEnumerator<Piece> GetEnumerator()
        {
            return new PieceEnumerator(this);
        }

        IEnumerator IEnumerable.GetEnumerator()
        {
            return new PieceEnumerator(this);
        }
    }




