    class StackAlbum
    {
        private static AsyncFileWriter asyncFw = AsyncFileWriter.Instance;
        const string PATH = @"..\stack.txt";
        private StackAlbum()
        {
            try
            {
                //string[] lines = File.ReadAllLines(PATH);
                //foreach (string line in lines)
                //{
                //    if (string.IsNullOrEmpty(line))
                //        continue;
                //    Add(new Album(line));
                //}
            }
            catch
            {
            }

        }
        private static StackAlbum _instance;
        private static volatile object _lock = new object();
        public void init(List<RealPiece> list)
        {
            if (pieces != null)
                return;
            misc.log("StackAlbum.Instance.init(albums);");
            var _list = new List<RealPiece>();
            foreach (RealPiece p in list)
            {
                if (p.Parent == null)
                {
                    if (p.DatesClassement.Count > 0)
                        _list.Add(p);
                }
            }


            _list.Sort(new LastModifClassementComparer());
            this.pieces = new HashList<RealPiece>(_list);

        }
        public static StackAlbum Instance
        {
            get
            {
                if (_instance == null)
                {
                    lock (_lock)
                    {
                        if (_instance == null)
                            _instance = new StackAlbum();
                    }
                }
                return _instance;
            }
        }

        private HashList<RealPiece> pieces;
        private int capacity;
        public int Capacity
        {
            set
            {
                if (capacity != value)
                {
                    capacity = value;
                }
            }
        }


        private bool enabled;
        public bool Enabled { set { enabled = value; } }

        public void build(bool incrementStack)
        {
            //int i = list.Count - 1;
            //int nb = 0;
            //int ii = list.Count;

            //for (; i >= 0; i--)
            //{
            //    nb++;
            //    if (!enabled || nb <= capacity)
            //        list[i].StackValue = nb;
            //    else
            //        list[i].StackValue = 0;
            //    list[i].StackIndex = nb;
            //}


            int i = pieces.Count - 1;
            int nb = 0;
            int ii = pieces.Count;

            var dict = new HashSet<Album>();

            for (; i >= 0; i--)
            {
                nb++;
                int val;

                if (!enabled)
                    val = 0;
                else if (nb <= capacity)
                    val = nb;
                else
                    val = 0;


                var p = pieces[i];

                foreach (Piece pp in p.Master)
                {
                    var album = pp.PieceAlbum;
                    if (album == null)
                        continue;
                    if (dict.Contains(album))
                        continue;

                    dict.Add(album);
                    album.StackValue = val;
                    album.StackIndex = nb;

                    if (incrementStack)
                    {
                        album.empeachedDecrement();
                    }
                }
            }


            var sb = new StringBuilder();
            foreach (var p in pieces)
            {
                sb.AppendLine(p.getLocation());
            }

            asyncFw.post(PATH, sb.ToString());
        }

        public void Add(Piece piece)
        {
            var p = piece.Master;
            if (pieces.Contains(p))
                pieces.Remove(p);
            pieces.Add(p);
        }

