        public static VirtualAlbumPiece getVirtual(ITrackInfoItunes track, RealPiece _real, string group, virtualMode mode)
        {
            //var _key = string.Format("{0}||{1}", key, _real.PieceId);
            //if (dict.ContainsKey(_key))
            //    return dict[_key];
            var v = new VirtualAlbumPiece(track, _real, group, mode);
            //dict.Add(_key, v);
            return v;
        }


        private VirtualAlbumPiece(ITrackInfoItunes track, RealPiece _real, string group, virtualMode mode)
            : base(track, _real)
        {
            this.mode = mode;
            this._group = group;
        }
        public override string Album
        {
            get
            {
                return _group;
            }
            set
            {

            }
        }

        public override int TrackNumber
        {
            get
            {
                return _trackNumber;
            }
            set
            {
                _trackNumber = value;
            }
        }

        public bool VirtualArtist
        {
            get
            {
                return mode == virtualMode.artist;
            }
        }
        public override virtualMode VirtualMode
        {
            get
            {
                return mode;
            }
        }

        public override string ToString()
        {
            return string.Format("{0} {1} {2}", mode, _group, base.ToString());
        }
    }
}

using System;
using pdb.obj;
using pdb.util;
using Math = pdb.util.Math; 

namespace pdb.gen.conf
