        public string Album
        {
            get
            {
                if (track.Virtual)
                {
                    if (track.VirtualMode == virtualMode.artist)
                        return string.Format("[{0}]", track.Album);
                    else if (track.VirtualMode == virtualMode.year)
                    {
                        if (track.Album.Length == 3)
                            return string.Format("[[{0}0']]", track.Album);
                        else
                            return string.Format("[[{0}]]", track.Album);
                    }
                    else if (track.VirtualMode == virtualMode.extended)
                        return string.Format("¤{0}", track.Album);
                    return track.Album;
                }
                return piece.Album;
            }
            set
            {
                if (!track.Virtual)
                {
                    if (value != Album)
                    {
                        piece.Album = value;
                        App.invalidateAlbums();
                    }
                }
                //if (value != track.Album)
                //{
                //    track.Album = value;
                //    onPropertyChanged("Album");
                //}
            }
        }

