using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.gen.albums
{
    class DirSort
    {
        private string path;
        static Dictionary<string, DirSort> dict = new Dictionary<string, DirSort>();
        private List<Piece> list = new List<Piece>();
        int level;
        static int count;
        static int maxlevel;
        const char SEP = '\\';
        private DirSort(string path, int level)
        {
            this.path = path;
            this.level = level;
        }

        public static void reset()
        {
            dict.Clear();
            count = 0;
            maxlevel = 0;
        }

        public override string ToString()
        {
            return string.Format("{0} level={1} count={2}", path, level, list.Count);
        }

        public static void record(Piece piece)
        {
            // ne sert à rien sauf pour le debug
            piece.TrackNumber = 0;
            foreach (var path in piece.RelativesPath)
            {
                var album = piece.PieceAlbum;
                bool go = true;
                if (album.VirtualMode == virtualMode.path)
                {
                    go = false;
                    var _name = album.Name.Remove(0, 1);
                    if (string.IsNullOrEmpty(_name) || path.StartsWith(_name))
                        go = true;
                }

                if (!go)
                    continue; 


                var tab = path.Split(SEP);
                var sb = new StringBuilder();
                int level = 0;
                foreach (string d in tab)
                {
                    if (string.IsNullOrEmpty(d))
                        continue;
                    if (sb.Length > 0)
                        sb.Append(SEP);
                    sb.Append(d);
                    level++;
                    if (level > maxlevel)
                        maxlevel = level;
                    var p = sb.ToString();
                    if (!dict.ContainsKey(p))
                        dict.Add(p, new DirSort(p, level));
                    dict[p].list.Add(piece);

                }
                // paths.Add(path);

            }
            count++;
        }

        public static void go()
        {
            int level = 0;
            int tn = 1;
            int nbb1 = 0;
            int nbb2 = 0;
            List<Piece> decote = new List<Piece>();
            while (true)
            {
                if (tn > count)
                    break;
                level++;
                if (level > maxlevel)
                    break;
                var listDir = dict.Values.Where(d => d.level == level);
                nbb1++;
                nbb2 = 0;
                //  bool modeCool = false;



                int oldTn = tn;
                nbb2++;
                int nbEvite = 0;

                foreach (DirSort dirsort in listDir)
                {
                    bool test = false;
                    //   var piece = dirsort.list[0];
                    foreach (Piece piece in dirsort.list)
                    {
                        if (piece.TrackNumber > 0 || test)
                        {
                            // liste de pls le suivant aurait pu être coché
                            if (test && piece.TrackNumber == 0)
                            {
                                decote.Add(piece);
                                nbEvite++;
                                break;
                            }
                            if (!test)
                                test = true;
                            continue;
                        }

                        piece.TrackNumber = tn;
                        tn++;
                        break;
                    }
                }



            }

            foreach (Piece piece in decote)
            {
                if (piece.TrackNumber == 0)
                {
                    piece.TrackNumber = tn;
                    tn++;
                }
                else
                {
                }
            }
        }
    }
    class PieceSort
    {
        public readonly Piece Piece;
        public PieceSort(Piece piece)
        {
            this.Piece = piece;
        }

    }
}

