            public bool AllFeeds { get { return allfeeds; } }
            public static string POUBELLE = Conf.Poubelle; 
            public CompacteurRead(Feeds feeds, Feed feed) { this.feed = feed; this.feeds = feeds;}
            public void compact()
            {
                POUBELLE = Conf.Poubelle;
