        public static bool Compact;
        public static string compact(string strClassement)
        {
            if (!Compact)
                return null;

            if (string.IsNullOrEmpty(strClassement))
                return null;
            if (ClassementHash.ZERO_KEY == strClassement)
                return null;
            return tradCompact[strClassement]; 
        }

