            recent = new ColorViewModel("recent", "album récemment noté", "#93c8ce", parent == null ? null : parent.recent, this);
            recentM = new ColorViewModel("recentM", "album récemment noté, prochain par coeff", "#93c8ce", parent == null ? null : parent.recentM, this);

            nextByCoeffMain = new ColorViewModel("nextByCoeffMain", "prochain morceau dans l'album en mode automatique causé par le coefficient principal", "#b5eaf0", parent == null ? null : parent.nextByCoeffMain, this);
