    class ContexteBuilder
    {

        public static ContexteBuilder Instance = new ContexteBuilder();
        private DateTime debut;
        private DateTime fin;
        DateTime date0;
        DateTime dateList;
        Contexte conf;
        private bool enabled;

        //private int nb;
        private int nbMax;
        timeSpan fd;

        //private bool findDebut(TrackInfoItunes t)
