    class NoteBuilder
    {
        private ListNote[] depot;
        private Note conf;
        private List<TrackSelector> selectors = new List<TrackSelector>();
        public NoteBuilder(Note conf)
        {
            this.conf = conf;
            foreach (SelectParamBase p in conf)
                selectors.Add(new TrackSelector(p));
            depot = new ListNote[conf.max - conf.min + 1];
            for (int i = 0; i <= conf.max - conf.min; i++)
            {
                depot[i] = new ListNote(i + conf.min);
            }
        }

        public void build(List<TrackInfoItunes> list)
        {
            foreach (TrackInfoItunes t in list)
                build(t);
        }


        private void build(TrackInfoItunes t)
        {
            double note = 0;

            foreach (TrackSelector ts in selectors)
            {
                if (ts.selectSimple(t))
                    note += (ts.Param as NoteItem).v;
            }
            ListNote d = null;
            if (note < conf.min)
                note = conf.min;
            if (note > conf.max)
                note = conf.max;

            int noteF = (int)note;
            d = depot[noteF - conf.min];
            d.Add(t);
        }

        public IEnumerable<ListNote> Depot { get { return depot; } }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Xml;
using pdb.util;

namespace pdb.podcast.Tuning
{
    public class Note : TuningBase, IEnumerable<NoteItem>
    {
        private List<NoteItem> items = new List<NoteItem>();
        /// <summary>
        /// Format du chemin des listes
        /// </summary>
        public string path;
        public int min = 0;
        public int max = 20;
        /// <summary>
        ///  Format du chemin des listes ne contenant que le premier item par niveau
        /// </summary>
        public string single;
        /// <summary>
        /// Format du chemin des listes ne contenant que le premier item d'un album - en admettant qu'on lise les items du niveau le plus haut au plus bas
        /// </summary>
        public string unique; 
        public Note()
        {
        }

        public Note(XmlElement node): base(node)
        {
            if (node == null)
                return; 
            builds("path", v => path = v);
            buildi("min", v => min = v);
            buildi("max", v => max = v);
            builds("single", v => single = v);
            builds("unique", v => unique = v);      
            items.Clear();
            foreach (XmlNode xitem in node.ChildNodes)
            {
                if (xitem is XmlElement)
                {
                    var item = new NoteItem(node, xitem as XmlElement);
                    items.Add(item);
                }
            }
        }

        public IEnumerator<NoteItem> GetEnumerator()
        {
            return items.GetEnumerator(); 
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return items.GetEnumerator(); 
        }
    }

    public class NoteItem : SelectParamBase
    {
        public double v;
        public NoteItem(XmlElement parent, XmlElement node)
            : base(parent, node)
        {
            buildd("v", _v => v = _v);
        }
        public NoteItem()
            : base()
        {
        }
    }
}
