        public void removeLastVersion()
        {
            int count = m_list.Count;
            if (count <= 1)
                return;
            m_list.RemoveAt(0);
            sortAndCompact();
        }

        public T LastValue
        {
            get
            {
                if (Empty)
                    return getNullValue();
                int count = m_list.Count;
                if (count < 2)
                    return getNullValue();
                int i = 1;
                while (true)
                {
                    var version = m_list[i];
                    //if (!version.Consider)
                    //{
                    //    i++;
                    //    if (i >= count)
                    //    {
                    //        return getNullValue();
                    //    }
                    //    continue;
                    //}
                    return version.Data.Value;
                }

            }

        }


        private bool Simple
