using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace pdb.gen.stat
{
    class ClassStruct
    {
        const int SIX = 6;
        const int NB = 10;
        private int[] tab = new int[NB];
        private int head;

        private int Level { get { return head + 4; } }

        private HashSet<decimal> occupés = new HashSet<decimal>();
        public List<int> getValues()
        {
            var list = new List<int>();
            for (int i = 0; i < Level && i < NB; i++)
                list.Add(tab[i]);
            return list;

        }
        public ClassStruct()
        {
            occupés.Add(0);
        }


        private decimal TruncateDecimal(decimal value, int precision)
        {
            decimal step = (decimal)Math.Pow(10, precision);
            decimal tmp = Math.Truncate(step * value);
            return tmp / step;
        }


        public void init(int head)
        {
            this.head = head;
        }

        public void position()
        {
            // voir si entamé
            //level 4 1.324.5 ==> level 2 1.4
            //  this.level = newLevel;
            bool hasMoved = false;
            for (int i = Level - 1; i < NB; i++)
            {
                if (tab[i] > 0)
                {
                    tab[i] = 0;
                    hasMoved = true;
                }
            }

            if (hasMoved)
            {
                //level 4 1.324.5 ==> level 2 1.4
                increment(this.Level - 1);
            }
            //else
            //    increment(newLevel); 

            //if (this.level != newLevel)
            //{
            //    int toto = 0; 
            //}


        }

        public bool isOccupe(decimal base10)
        {
            var arrondi = TruncateDecimal(base10, Level-1);
            return occupés.Contains(arrondi);
        }
        public bool isOccupe()
        {
            return occupés.Contains(getValueBase10());
        }

        // y a un loup on rentre un nombre 10 fois plus petit
        public void occupe(decimal base10)
        {
            var arrondi = TruncateDecimal(base10, Level-1);
            var added = occupés.Add(arrondi);
            if (added)
            {
            }
        }

        public void move()
        {
            increment(this.Level);
        }



        public decimal getValue()
        {
            decimal k = 1m;
            decimal ret = 0m;
            for (int i = 0; i < Level; i++)
            {
                ret += k * tab[i];
                k /= SIX;
            }
            return ret;
        }

        public decimal getValueBase10()
        {
            decimal k = 1m;
            decimal ret = 0m;
            for (int i = 0; i < Level; i++)
            {
                ret += k * tab[i];
                k /= 10;
            }
            return ret;
        }

        public decimal getNextValue(int level)
        {
            decimal k = 1;
            decimal ret = 0;
            for (int i = 0; i < level && i < NB; i++)
            {
                var v = tab[i];
                if (i == level - 1)
                    v++;
                ret += k * v;
                k /= SIX;
            }
            return ret;
        }



        private void increment(int level)
        {
            if (level < head)
                head = level;
            int index = level - 1;
            if (index < 0)
                return;



            int v = tab[index];
            v++;
            if (v < SIX)
            {
                tab[index] = v;
                //if (occupés.Contains(getValueBase10()))
                //    increment(l); 
            }
            else
            {
                tab[index] = 0;
                increment(level - 1);
            }


        }

        public override string ToString()
        {
            StringBuilder sb = new StringBuilder();

            for (int i = 0; i < Level && i < NB; i++)
            {

                sb.Append(tab[i]);
                if (i == Level - 2)
                    sb.Append(".");
                else if ((i + 1) % 4 == 0)
                    sb.Append(" ");
            }

            //sb.Append(" reste:");
            //for (int i = level; i < NB; i++)
            //{
            //    sb.Append(" ");
            //    sb.Append(tab[i]);
            //}


            sb.Append(" head:");
            sb.Append(head);

            return sb.ToString();

        }
    }
}

