        public int getNb(bool load, LimitItemBase conf)
        {
            if (load)
                return LoadNb;
            int __nb = nb;
            int __nb2 = nb2;
            if (!overwriteConf && conf.nb2 < __nb2)
                __nb2 = conf.nb2;
            if (__nb2 < __nb)
                __nb = __nb2;
            return __nb; 

        }


