        {
            int __nb = nb;
            int __nb2 = nb2;
            if (!overwriteConf && conf.nb2 < __nb2)
                __nb2 = conf.nb2;
            if (__nb2 < __nb)
                __nb = __nb2;
            return __nb;

        }

        public double getFd(bool load, LimitItemBase conf)
        {
            if (load)
                return LoadFeedDuration;
            double __fd = FeedDuration ;
            double __fd2 = fd2;
            if (!overwriteConf && conf.fd2 < __fd2)
                __fd2 = conf.nb2;
            if (__fd2 < __fd)
                __fd = __fd2;
            return __fd;

        }

