            double __fd = FeedDuration;
            double __fd2 = fd2;
            if (!overwriteConf && conf.fd2 < __fd2)
                __fd2 = conf.nb2;
            if (__fd2 < __fd)
                __fd = __fd2;
            return __fd;

        }

