                    int index = 1;
                    var rankMax = 0; 
                    for (int i = 0; i < newElements3.Count; i++)
                    {
                        var ifile = newElements2[i];

                        var t = TrackListViewModel.get(ifile.PieceId); // findTrack(this, ifile.PieceId);
