namespace pdb.player.Vue.Conf
{
    /// <summary>
    /// Interaction logic for ConfMedianneW.xaml
    /// </summary>
    public partial class ConfMedianneW : UserControl
    {

        private ConfMedianne conf;
        private ConfVirt main;
        private ConfVirt min;
        private IConfSort parent;
        public ConfMedianneW()
        {
            InitializeComponent();

        }

        public ConfMedianneW(ConfMedianne conf, IConfSort parent)
            : this()
        {
            this.conf = conf;
            this.parent = parent;

            this.main = new ConfVirt(conf.Main, parent);
            this.min = new ConfVirt(conf.Min, parent);

            gmain.Children.Add(main);
            gmin.Children.Add(min);

            InitializeComponent();

        }

        public void checkIdem()
        {
            if (parent.DuplicateItem)
            {
                conf.makeIdem();
                min.IsEnabled = false;
            }
            else
            {
                min.IsEnabled = true;
                conf.duplicateMainIf();
            }


            min.Conf = conf.Min;
        }

        public void display()
        {



            cb_enabled.IsChecked = conf.enabled;
            cbxSort.SelectedIndex = (int)conf.type;
            tb_medianne.Text = conf.medianne.ToString();
           // cb_quasi.IsChecked = conf.quasi;

            cb_removeFirst.IsChecked = conf.removeFirst;
            cb_deleteFirst.IsChecked = conf.deleteFirst;
            cbx1_removeLast.SelectedIndex = (int)conf.removeLast;

            tb_exp.Text = conf.expEnabled.ToString();
            tb_zero.Text = Piece.getClassementEquiv(conf.zero);
            cb_removeEnabled.IsChecked = conf.removeEnabled;

            cbx_cmpMinn.SelectedIndex = (int)conf.cmpMin;

            main.display();
            checkIdem();
            min.display();

        }

        public void record()
        {

            conf.enabled = cb_enabled.IsChecked.Value;
            conf.type = (medianneMode)cbxSort.SelectedIndex;
            conf.medianne = Convert.ToDecimal(tb_medianne.Text);
           // conf.quasi = cb_quasi.IsChecked.Value;

            conf.removeFirst = cb_removeFirst.IsChecked.Value;
            conf.deleteFirst = cb_deleteFirst.IsChecked.Value;
            conf.removeLast = (removeLast)cbx1_removeLast.SelectedIndex;

            conf.expEnabled = Convert.ToDecimal(tb_exp.Text);
            var cl = Classement.create(tb_zero.Text);
            conf.zero = cl.RapportClassement;
            conf.removeEnabled = cb_removeEnabled.IsChecked.Value;

            conf.cmpMin = (modeMin)cbx_cmpMinn.SelectedIndex;

            main.record();
            if (!parent.DuplicateItem)
                min.record();



        }

        public void onchange()
        {
            main.onchange();
            min.onchange();
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using Math0 = System.Math;

namespace pdb.util
{
    public static class Math
    {
        static readonly double max;
        static readonly double min;
        static Math()
        {
            max = Convert.ToDouble(decimal.MaxValue);
            min = -max;
        }
        public static decimal TotalMilliseconds(this TimeSpan ts)
        {
            return ts.TotalMilliseconds.toDecimal();
        }

        public static decimal TotalDays(this TimeSpan ts)
        {
            return ts.TotalDays.toDecimal();
        }
        public static double toDouble(this decimal x)
        {
            return Convert.ToDouble(x);
        }

        public static decimal toDecimal(this double x)
        {
            try
            {
                return Convert.ToDecimal(x);
            }
            catch
            {
                if (x >= max)
                    return decimal.MaxValue;
                if (x <= min)
                    return decimal.MinValue;
                return decimal.Zero;
            }
        }
        public static decimal toDecimal(this long x)
        {
            return Convert.ToDecimal(x);
        }
        public static decimal Abs(decimal x)
        {
            return Math0.Abs(x);
        }
        public static double Abs(double x)
        {
            return Math0.Abs(x);
        }

        public static int Abs(int x)
        {
            return Math0.Abs(x);
        }

        public static decimal Exp(decimal d)
        {
            return Math0.Exp(d.toDouble()).toDecimal();
        }

        public static decimal Log(decimal d)
        {
            return Math0.Log(d.toDouble()).toDecimal();
        }

        public static decimal Log10(decimal d)
        {
            return Math0.Log10(d.toDouble()).toDecimal();
        }

        public static decimal Log(decimal x, decimal y)
        {
            return Math0.Log(x.toDouble(), y.toDouble()).toDecimal();
        }

        public static decimal Pow(decimal x, decimal y)
        {
            return Math0.Pow(x.toDouble(), y.toDouble()).toDecimal();
        }

        public static double PowD(double x, double y)
        {
            return Math0.Pow(x, y);
        }


        //  public static int Pow(int x, int y)
        //{
        //    return (int)Math0.Pow(x,y); 
        //}






        public static decimal Round(decimal x, int nbDec)
        {
            return Math0.Round(x, nbDec);
        }
        public static double Round(double x, int nbDec, MidpointRounding mode)
        {
            return Math0.Round(x, nbDec, mode); 
        }
        public static double Round(double x)
        {
            return Math0.Round(x); 
        }

        public static int Min(int x, int y)
        {
            return Math0.Min(x, y);
        }
        public static decimal Min(decimal x, decimal y)
        {
            return Math0.Min(x, y);
        }
        public static double Floor(double x)
        {
            return Math0.Floor(x); 
        }

        public static decimal Max(decimal x, decimal y)
        {
            return Math0.Max(x, y); 
        }


    }
}

