        //    sb2.Append(conf2.CurrentStaticm.FormatE(3));
        //    check(sb, sb2);

        //    sb.Append(conf.Serial);
        //    sb2.Append(conf2.Serial);
        //    check(sb, sb2);
        //    //  sb.Append(conf.SerialBox); sb.Append(" ");
        //    // sb.Append(conf.Alt); sb.Append(" ");
