using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Windows.Media;

namespace pdb.player.ViewModel.Colors
{
    public delegate bool colorvm(ColorViewModel vm);

    /// <summary>
    /// Paramètre de couleur
    /// </summary>
    public class ColorViewModel : BibViewModelTreelBase, IEnumerable<ColorViewModel>
    {
        /// <summary>
        /// Nom du paramètre
        /// </summary>
        protected string name;
        /// <summary>
        /// Code couleur
        /// </summary>
        private string code;

        public readonly string defaultCode;

        public string Description { get; private set; }


        /// <summary>
        /// Propriétaire (père dans l'arborescence)
        /// </summary>
        private ColorViewModel owner;

        private static ColorViewModel selected;
        public static event EventHandler selectedChanged;
        public event EventHandler colorChange;
        public static event EventHandler colorChangeTree;

        public ColorViewModel Selected
        {
            get
            {
                return selected;
            }

            set
            {
                if (value != selected)
                {
                    selected = value;
                    OnPropertyChanged("Selected");
                    if (selectedChanged != null)
                        selectedChanged(this, EventArgs.Empty);
                }
            }
        }

        public override bool IsSelected
        {
            get
            {
                return base.IsSelected;
            }
            set
            {
                base.IsSelected = value;
                if (value)
                    Selected = this;
            }
        }



        /// <summary>
        /// Père pour l'évaluation des paramètres 
        /// </summary>
        public ColorViewModel Parent { get { return _parent as ColorViewModel; } }
        protected List<ColorViewModel> subs = new List<ColorViewModel>();


        public ColorViewModel(string name, string description, ColorViewModel parent, ColorViewModel prop)
            : base(parent)
        {
            this.name = name;
            this.Description = description;
            this.owner = prop;
            if (prop != null)
                prop.subs.Add(this);
        }

        public ColorViewModel(string name, string description, string defaultCode, ColorViewModel parent, ColorViewModel prop)
            : this(name, description, parent, prop)
        {

            this.defaultCode = defaultCode;
            this.Code = "";

        }

        public string Name { get { return name; } }
        protected  void OncolorChangeTree()
        {
            if (colorChangeTree != null)
                colorChangeTree(this, EventArgs.Empty);

        }
        public string Code
        {
            get
            {
                return code;
            }
            set
            {
                if (value != code)
                {
                    code = value;
                    if (code == defaultCode)
                        code = "";
                    else if (Parent != null && Parent.ArboCode == ArboCode)
                        code = "";
                    //_color = default(Color);
                    //_brush = null;
                    OnPropertyChanged("Code");
                    OnPropertyChanged("ArboCode");
                    OnPropertyChanged("Color");
                    OnPropertyChanged("Brush");
                    OnPropertyChanged("Ancestor"); 

                    if (colorChange != null)
                        colorChange(this, EventArgs.Empty);

                    OncolorChangeTree();


                }
            }
        }

        ColorViewModel ancestor;

        public string Ancestor
        {
            get
            {
                var v = ArboCode;
                if (v == defaultCode)
                    return "valeur par defaut";
                if (ancestor == this)
                    return "";
                return "Hérité de " +ancestor.ArboName; 
            }
        }

        public double getDouble() { try { return Convert.ToDouble(ArboCode); } catch { return 0; } }
        public double geint() { try { return Convert.ToInt32(ArboCode); } catch { return 0; } }

        public string ArboCode
        {
            get
            {
                string ret = null;
                ColorViewModel current = this;

                while (current != null)
                {
                    ancestor = current; 
                    if (string.IsNullOrEmpty(current.code) || defaultCode == current.code)
                    {
                        current = current.Parent;
                    }
                    else
                    {
                        ret = current.code;
                        break; 
                    }
                }

                if (string.IsNullOrEmpty(ret))
                    return defaultCode; 

                return ret;
            }
        }

        public string ArboName
        {
            get
            {

                if (owner == null || string.IsNullOrEmpty(owner.Name))
                    return name;
                return owner.ArboName + "." + name;
            }
        }

        public void reset()
        {
            Code = "" ;
        }
        public void resetTree()
        {
            tree((vm) => { vm.reset(); return false; });
        }

        public bool tree(colorvm action)
        {
            if (action(this))
                return true;
            foreach (ColorViewModel sub in subs)
            {
                if (sub.tree(action))
                    return true;
            }
            return false;
        }




        public IEnumerator<ColorViewModel> GetEnumerator()
        {
            return subs.GetEnumerator();
        }

        System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator()
        {
            return subs.GetEnumerator();
        }

    //    private Color _color;
        public Color Color
        {
            get
            {
                //if (_color == default(Color))
                //{
                    var code = ArboCode;
                    if (!string.IsNullOrEmpty(code) && code.Length > 3)
                    {
                        try
                        {
                         return  (Color)ColorConverter.ConvertFromString(code);
                        }
                        catch { }

                    }
                //}
                return default(Color);
            }
        }

     //   private SolidColorBrush _brush;
        public SolidColorBrush Brush
        {
            get
            {
                //if (_brush == null)
                //{
                //    _brush = new SolidColorBrush(Color);
                //}
                return new SolidColorBrush(Color); ;
            }
        }

        public override string ToString()
        {
            return string.Format("{0}={1} ({2} {3})", ArboName, Code, ArboCode, Color); 
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.db.obj;
using pdb.gen;

namespace pdb.player.ViewModel.Commande
{
    class DeleteCmd : CommandeBase
    {
        public override bool CanExecute(object parameter)
        {
            var track = TrackListViewModel.HumanSelected;
            return track != null;
        }

        public override void Execute(object parameter)
        {
            var track = TrackListViewModel.HumanSelected;

            bool? byAlbum = App.Instance.TriChoix(string.Format("supprimer l'abum entier {0} {1} ?", track.Album, track), "Suppression");
            if (byAlbum == null)
                return;
            if (byAlbum == true)
            {
                int nb = 0;
                var album = App.gen.getTracksAlbumByNumber(track.PieceGen);
                foreach (var p in album)
                {
                    bool? go = deleteP(p); // App.Instance.TriChoix("suppression de " + p + " (track n°" + p.TrackNumber + ")?", "Suppression");
                    if (go == null)
                        break;
                    if (go == false)
                        continue;

                    nb++;
                }
                App.Instance.InfoIHm(nb + " éléments supprimés", "Suppression");
            }
            else
                deleteP(track.PieceGen); 

            App.go();
        }

        private bool? deleteP(Piece p)
        {
            bool? go = null; 
            if (p.PieceParentId < 0)
            {
                var sb = new StringBuilder();
                var list = new List<Piece>(p);

                foreach (Piece sub in list)
                {
                    sb.Append("ID=");
                    sb.Append(sub.PieceId);
                    sb.Append(" ");
                    sb.AppendLine(sub.Album);
                }

                go = App.Instance.TriChoix(String.Format("Attention ! Morceau maitre! Suppression en cascade du track ID={0} {1} {2}  \n{3}", p.PieceId, p.Name, p.Album, sb.ToString()), "Suppression en Cascade");
                if (go == null)
                    return null;
                if (go == false)
                {
                    // récupération des données pour les enfants
                    foreach (Piece c in p.Childs)
                    {
                        (c.Track as CPiece).Parent = null;
                    }
                    App.Db.List.delete(p.Track as CPiece);

                    return true; 
                }
                else
                {
                    foreach (Piece c in p.Childs)
                    {
                        App.Db.List.delete(c.Track as CPiece);
                    }
                    App.Db.List.delete(p.Track as CPiece);

                    return true; 
                }
            }
            else
            {
                go= App.Instance.TriChoix(String.Format("Suppression du track ID={0} {1} {2}", p.PieceId, p.Name, p.Album), "Suppression");
                if (go == null)
                    return null;
                if (go == false)
                    return false;
                App.Db.List.delete(p.Track as CPiece);

            }

            return go; 
        }
    }
}

