using System;
using System.Collections.Generic;
using System.Threading;
using pdb.it;
using pdb.obj;
using pdb.util;
using pdb.gen.conf;
using pdb.gen.Tuning;
using PlayList = pdb.it.PlayList; 

namespace pdb.gen.it
{
    public class iTListWriter : IListWriter, IAbortTask
    {
        private Loader loader;
        private PlayListComposite currentPlayList;
        private string currentTrack;
        public iTListWriter(Loader loader)
        {
            this.loader = loader;
        }

        public void initCurrent()
        {
            var _track = loader.CurrentTrackLocation;
            if (!string.IsNullOrEmpty(_track))
                currentTrack = _track;
            var pl = loader.CurrentPlaylist;
            if (pl != null)
                currentPlayList = pl;
            misc.log("initCurrent::currentTrack:'{0}' currentPl:'{1}'", currentTrack, currentPlayList);
        }


        public void writePlayList(string folder, string name, ICollection<IFile> list, bool withOrder, IBgTask task)
        {
            var conf = CConf.ConfGen;

            if (!conf.GenE)
            {
                if (name.StartsWith("e"))
                    return;
            }
            if (!conf.GenLarge)
            {
                if (name.EndsWith("+"))
                    return;
            }

            // misc.log(">< writePlaylist {0}", task);
            string[] folders = new string[] { folder };
            if (folder.Contains(@"\"))
            {
                folders = folder.Split('\\');
            }

            pdb.it.PlayList current = null;
            foreach (string f in folders)
            {
                if (aborted)
                    return; 
                current = loader.getOrCreatePlayList(current, f, true);
            }


            //   PlayListComposite plFolder = loader.getOrCreateFolder(folder);
            if (aborted)
                return; 
            var playlist = loader.getOrCreatePlayList(current, name, false);

            // CPlayList playlist = loader.getList(plFolder, name);//  getList(folder, name);
            // return loader.mergeList(playlist, list, withOrder,this);
            var truncateList = truncate(playlist, list);
            if (aborted)
                return; 
            if (withOrder)
                playlist.mergeListWithOrder(truncateList, this, task);
            else
                playlist.mergeList(truncateList, this);



        }

        private ICollection<IFile> truncate(PlayList pl, ICollection<IFile> list)
        {

            ConfGen conf = CConf.ConfGen;
            int max = conf.ListLimit;
            var a_list = new List<IFile>(list);
            var newList = new List<IFile>();

            //if (max < a_list.Count)
            //{
            a_list = new List<IFile>(a_list);
            a_list.Reverse();
            //}

            int indexFound = -1;
            int count = a_list.Count;
            if (currentPlayList == pl)
            {
                initCurrent();
                var reading = pl.getPieceInList(currentTrack) as TrackInList;
                if (reading == null)
                    reading = loader.CurrentTrackInList;

                if (reading != null)
                {
                    var currentFile = a_list.Find(f => f.Location.Equals(reading.Location));
                    if (currentFile != null)
                    {
                        indexFound = a_list.IndexOf(currentFile);
                        var listNext = new List<IFile>();
                        listNext.Add(currentFile);

                        for (int iNext = indexFound + 1; iNext < count; iNext++)
                        {
                            IFile next = a_list[iNext];
                            listNext.Add(next);
                        }

                        pl.recordNextTracks(listNext);

                    }


                }

            }
            int i0 = 0;
            int i1 = 0;

            if (indexFound > 0)
            {
                i0 = indexFound - conf.ListReaderBefore;
                i1 = indexFound + conf.ListReaderAfter;

            }


            for (int i = 0; i < count; i++)
            {
                if (i >= max && i > i1)
                    break;

                if (i < max || (i >= i0 && i <= i1))
                {
                    IFile file = a_list[i];
                    newList.Add(file);
                }



            }

            return newList;
        }



        #region IListWriter Members

        public String Type { get { return "native"; } }




        #endregion



        #region ICloneable Members

        public object Clone()
        {
            return new iTListWriter(loader);
        }

        #endregion





        #region IAbortTask Members
        private bool aborted;
        public bool Aborted
        {
            get
            {
                return aborted;
            }
            set
            {
                aborted = value;
            }
        }

        #endregion
    }
}

using pdb.gen.conf;
using pdb.iit;
using pdb.it;
using pdb.obj;
using PlayList = pdb.it.PlayList; 

namespace pdb.gen.it
{
    public class PlayListFilter : IPlayListFilter
    {

        #region IPlayListFilter Members

        public bool consider(PlayListComposite current)
        {
            var pl = current as PlayList;
            var conf = CConf.ConfGen;

            bool withOrder = conf.WithOrder;

            string name = current.Name;

            if (name.EndsWith("+"))
                withOrder = withOrder && conf.OrderLarge;
            var str = name.Replace("+", "");

            if (str.EndsWith("0"))
                withOrder = withOrder && conf.OrderZero;

            if (str.Contains("precision"))
                withOrder = withOrder && conf.OrderZero;

            if (name.Contains("check0"))
                withOrder = false;

            if (conf.AlbumOrdo && current.isSubList("album"))
                withOrder = true;
            //if (current.isSubList("album"))
            //    withOrder = true;

            pl.VerifyDoublon = !withOrder;


            if (current.isSubList("classement"))
            {
                pl.VerifyDoublon = true;
                return true;
            }
            if (current.isSubList("gen"))
            {
                if (name.Contains("check0"))
                {
                    if (conf.GenCheck)
                        return true;
                    else
                        return false;
                }
                bool ret = false;
                bool withTimeOrder = false;

                foreach (var mode in conf.Modes)
                {
                    if (mode.extend)
                    {
                        string rep = mode.ToString();
                        if (current.isSubList(rep))
                        {
                            withTimeOrder = mode.extendTimeOrder;
                            ret = true;
                            break;
                        }
                    }
                }

                if (ret)
                {
                    foreach (var l in conf.Listes)
                    {
                        if (l.extend)
                        {
                            if (withTimeOrder)
                            {
                                if (l.extendTimeOrder)
                                    pl.VerifyDoublon = false;
                            }
                            if (name.Contains(l.ToString()))
                            {

                                if (name.StartsWith("e"))
                                {
                                    if (!conf.GenE)
                                        return false;
                                    string level = "";
                                    if (l.level)
                                        level = "_";
                                    string pat = "e" + level + l.ToString();
                                    string pat2 = pat + "+";

                                    if (name.Equals(pat))
                                        return true;
                                    else if (name.Equals(pat2))
                                        return true;
                                }
                                if (name.EndsWith("+") && !conf.GenLarge)
                                    return false;

                                if (name.StartsWith("g"))
                                {
                                    string level = "";
                                    if (l.level)
                                        level = "_";
                                    string pat = "g" + level + l.ToString();
                                    string pat2 = pat + "+";
                                    if (name.Equals(pat))
                                        return true;
                                    else if (name.Equals(pat2))
                                        return true;
                                }

                            }
                        }
                    }
                }

                if (ret)
                    return false;

                // gestion precision

                if (current.isSubList("precision") && conf.PrecisionMin.extend)
                {

                    for (int precision = conf.PrecisionMin.item; precision <= conf.PrecisionMax.item && precision > 0; precision++)
                    {

                        if (name.Contains(precision.ToString()))
                        {
                            if (name.EndsWith("+") && !conf.GenLarge)
                                return false;
                            return true;
                        }
                    }
                }
            }


            return false;
        }

        #endregion
    }
}

using System;
using System.Collections.Generic;
using pdb.util;

namespace pdb.obj
{
    public class Folder : PlayList
    {
        private Folder folder;
        private List<Folder> folders = new List<Folder>();
        private List<PlayList> lists = new List<PlayList>();

        public Folder(string name)
            : base(name)
        {
        }


        public virtual void add(Folder subFolder)
        {
            folders.Add(subFolder);
            subFolder.folder = this;  
        }

        public virtual void add(PlayList pl)
        {
            lists.Add(pl);
            pl.setFolder(this); 
        }

        public bool HasSubComposites
        {
            get
            {
                return folders.Count > 0; 
            }
        }




        public override string ToString()
        {
            return getPath();          
        }





        public String Name { get { return name; } set { name = value; } }

        public bool isSubList(string a_name)
        {
            if (name == a_name)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(a_name);
        }

        public bool isSubList(Folder other)
        {
            if (other == this)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(other);
        }



        public virtual void DeleteChilds()
        {
            lock (this)
            {
                try
                {
                    foreach (PlayList pl in new List<PlayList>(lists))
                    {
                        pl.DeleteBase(); 
                    }

                    foreach (Folder sub in new List<Folder>(folders))
                    {
                        folder.DeleteChilds(); 
                    }


                }
                catch (Exception e)
                {
                    misc.log(e.ToString());
                    throw;
                }
            }

        }


        public List<Folder> SubFolders { get { return folders; } }
        public List<PlayList> PlayLists { get { return lists; } }



    }

}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using pdb.util;

namespace pdb.obj
{
    public interface IItem
    {
        int ID { get; set; }
    }
    public class FolderBase : IItem
    {
        protected String name = "";
        protected int id;
        protected PlayList folder;

        public FolderBase(String name)
        {
            this.name = name;
        }
        public FolderBase(String name, PlayList folder)
        {
            this.name = name;
            this.folder = folder;
        }
        public string getPath()
        {
            if (folder == null) return name;
            return folder.getPath() + SEP + name;
        }
        public int ID { get { return id; } set { id = value; } }

        public string Name { get { return name; } set { name = value; } }


        public bool isSubList(string a_name)
        {
            if (name == a_name)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(a_name);
        }

        public override string ToString()
        {
            return getPath(); 
        }


        public const char SEP = '\\';

    }
    public class PlayList : FolderBase
    {
      //  private PlayList folder;
        private List<IItem> items = new List<IItem>();
        private List<PlayList> lists = new List<PlayList>();


        public PlayList(string name)
            : base(name)
        {
        }

        public PlayList(string name, PlayList parent)
            : base(name, parent)
        {
            if (parent != null)
                parent.lists.Add(this); 
        }
        public void setFolder(PlayList folder)
        {
            this.folder = folder;
        }

        public void DeleteBase()
        {
            items.Clear();
        }
        public List<IItem> Items { get { return items; } }

        public virtual void add(PlayList pl)
        {
            lists.Add(pl);
            pl.setFolder(this);
        }

        public virtual void add(IItem item)
        {
            items.Add(item);

        }

        public bool isSubList(string a_name)
        {
            if (name == a_name)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(a_name);
        }

        public bool isSubList(PlayList other)
        {
            if (other == this)
                return true;
            if (folder == null)
                return false;
            return folder.isSubList(other);
        }



        public virtual void DeleteChilds()
        {
            lock (this)
            {
                try
                {
                    foreach (PlayList pl in new List<PlayList>(lists))
                    {
                        pl.DeleteBase();
                    }

                }
                catch (Exception e)
                {
                    misc.log(e.ToString());
                    throw;
                }
            }

        }



        public List<PlayList> PlayLists { get { return lists; } }


    }
}
